  "bugs": {
    "url": "https://github.com/nrwl/nx-console/issues"
  },
  "keywords": [
    "nx",
    "monorepo",
    "ai",
    "mcp",
    "model",
    "context",
    "protocol"
  ],
  "bin": "main.js",
  "files": [
    "main.js",
    "README.md"
  ],
  "nx": {
    "name": "nx-mcp",
    "projectType": "application",
    "sourceRoot": "apps/nx-mcp/src",
    "tags": [
      "type:mcp"
    ],
    "targets": {
      "build": {
        "executor": "@nx/esbuild:esbuild",
        "outputs": [
          "{options.outputPath}"
        ],
        "defaultConfiguration": "production",
        "options": {
          "platform": "node",
          "outputPath": "dist/apps/nx-mcp",
          "outputFileName": "main.js",
          "format": [
            "cjs"
          ],
          "thirdParty": true,
          "main": "apps/nx-mcp/src/main.ts",
          "tsConfig": "apps/nx-mcp/tsconfig.app.json",
          "sourcemap": false,
          "minify": true,
          "assets": [
            "apps/nx-mcp/README.md"
          ],
          "esbuildOptions": {
            "mainFields": [
              "module",
              "main"
            ],
            "treeShaking": true,
            "outExtension": {
              ".js": ".js"
            }
          }
        },
        "configurations": {
          "production": {},
          "debug": {
            "minify": false,
            "sourcemap": true
          }
        }
      },
      "setup-publish": {
        "command": "node setup-publish.js",
        "options": {
          "cwd": "apps/nx-mcp"
        },
        "dependsOn": [
          "build"
        ]
      }
    }
  }
}

