import { HashPlanInspector } from './hash-plan-inspector';
import { ProjectGraph } from '../config/project-graph';
import { NxJsonConfiguration } from '../config/nx-json';
import { join } from 'path';
import { tmpdir } from 'os';
import { mkdirSync, writeFileSync, rmSync } from 'fs';

describe('HashPlanInspector', () => {
  let inspector: HashPlanInspector;
  let projectGraph: ProjectGraph;
  let nxJson: NxJsonConfiguration;
  let tempWorkspaceRoot: string;

  beforeEach(() => {
    // Create a temporary workspace directory
    tempWorkspaceRoot = join(tmpdir(), `nx-test-${Date.now()}`);
    mkdirSync(tempWorkspaceRoot, { recursive: true });

    // Create a minimal nx.json
    nxJson = {
      extends: undefined,
      affected: {
        defaultBase: 'main'
      },
      tasksRunnerOptions: {
        default: {
          runner: '@nx/workspace/tasks-runners/default',
          options: {}
        }
      },
      targetDefaults: {},
      namedInputs: {
        default: ['{projectRoot}/**/*'],
        production: ['default']
      },
      workspaceLayout: {
        appsDir: 'apps',
        libsDir: 'libs'
      }
    };

    writeFileSync(
      join(tempWorkspaceRoot, 'nx.json'),
      JSON.stringify(nxJson, null, 2)
    );

    // Create a simple project graph
    projectGraph = {
      nodes: {
        'test-app': {
          name: 'test-app',
          type: 'app',
          data: {
            root: 'apps/test-app',
            sourceRoot: 'apps/test-app/src',
            targets: {
              build: {
                executor: '@nx/webpack:webpack',
                options: {
                  outputPath: 'dist/apps/test-app'
                }
              },
              test: {
                executor: '@nx/jest:jest',
                options: {
                  jestConfig: 'apps/test-app/jest.config.ts'
                }
              }
            }
          }
        },
        'test-lib': {
          name: 'test-lib',
          type: 'lib',
          data: {
            root: 'libs/test-lib',
            sourceRoot: 'libs/test-lib/src',
            targets: {
              build: {
                executor: '@nx/js:tsc',
                options: {
                  outputPath: 'dist/libs/test-lib'
                }
              },
              test: {
                executor: '@nx/jest:jest',
                options: {
                  jestConfig: 'libs/test-lib/jest.config.ts'
                }
              }
            }
          }
        }
      },
      dependencies: {
        'test-app': [
          {
            source: 'test-app',
            target: 'test-lib',
            type: 'static'
          }
        ],
        'test-lib': []
      },
      allWorkspaceFiles: [],
      fileMap: {
        projectFileMap: {},
        nonProjectFiles: []
      },
      layout: {
        appsDir: 'apps',
        libsDir: 'libs'
      }
    };

    // Create project directories
    mkdirSync(join(tempWorkspaceRoot, 'apps', 'test-app', 'src'), { recursive: true });
    mkdirSync(join(tempWorkspaceRoot, 'libs', 'test-lib', 'src'), { recursive: true });

    // Create some test files
    writeFileSync(
      join(tempWorkspaceRoot, 'apps', 'test-app', 'src', 'main.ts'),
      'console.log("Hello World");'
    );
    writeFileSync(
      join(tempWorkspaceRoot, 'libs', 'test-lib', 'src', 'index.ts'),
      'export const lib = () => "test-lib";'
    );

    inspector = new HashPlanInspector(projectGraph, tempWorkspaceRoot, nxJson);
  });

  afterEach(() => {
    // Clean up temporary directory
    if (tempWorkspaceRoot) {
      rmSync(tempWorkspaceRoot, { recursive: true, force: true });
    }
  });

  describe('constructor', () => {
    it('should create an instance with required dependencies', () => {
      expect(inspector).toBeDefined();
      expect(inspector).toBeInstanceOf(HashPlanInspector);
    });

    it('should handle default workspace root when not provided', () => {
      const inspectorWithDefaults = new HashPlanInspector(projectGraph);
      expect(inspectorWithDefaults).toBeDefined();
    });

    it('should read nx.json when not provided', () => {
      const inspectorWithDefaults = new HashPlanInspector(projectGraph, tempWorkspaceRoot);
      expect(inspectorWithDefaults).toBeDefined();
    });
  });

  describe('init', () => {
    it('should initialize the inspector without errors', async () => {
      await expect(inspector.init()).resolves.not.toThrow();
    });

    it('should be able to call init multiple times', async () => {
      await inspector.init();
      await expect(inspector.init()).resolves.not.toThrow();
    });
  });

  describe('inspectHashPlan', () => {
    beforeEach(async () => {
      await inspector.init();
    });

    it('should inspect hash plan for single project and target', () => {
      const result = inspector.inspectHashPlan(['test-app'], ['build']);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should inspect hash plan for multiple projects', () => {
      const result = inspector.inspectHashPlan(['test-app', 'test-lib'], ['build']);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should inspect hash plan for multiple targets', () => {
      const result = inspector.inspectHashPlan(['test-app'], ['build', 'test']);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should handle configuration parameter', () => {
      const result = inspector.inspectHashPlan(['test-app'], ['build'], 'production');

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should handle overrides parameter', () => {
      const overrides = { watch: true };
      const result = inspector.inspectHashPlan(['test-app'], ['build'], undefined, overrides);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should handle extraTargetDependencies parameter', () => {
      const extraTargetDependencies = {
        build: [{ target: 'test', projects: 'self' }]
      };
      const result = inspector.inspectHashPlan(
        ['test-app'], 
        ['build'], 
        undefined, 
        {}, 
        extraTargetDependencies
      );

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should handle excludeTaskDependencies parameter', () => {
      const result = inspector.inspectHashPlan(
        ['test-app'], 
        ['build'], 
        undefined, 
        {}, 
        {}, 
        true
      );

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should handle empty project names array', () => {
      const result = inspector.inspectHashPlan([], ['build']);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should handle empty targets array', () => {
      const result = inspector.inspectHashPlan(['test-app'], []);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });
  });

  describe('inspectTask', () => {
    beforeEach(async () => {
      await inspector.init();
    });

    it('should inspect a single task', () => {
      const target = { project: 'test-app', target: 'build' };
      const result = inspector.inspectTask(target);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should inspect task with configuration', () => {
      const target = { project: 'test-app', target: 'build', configuration: 'production' };
      const result = inspector.inspectTask(target);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should handle parsed args parameter', () => {
      const target = { project: 'test-app', target: 'build' };
      const parsedArgs = { watch: true, verbose: true };
      const result = inspector.inspectTask(target, parsedArgs);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should handle extraTargetDependencies parameter', () => {
      const target = { project: 'test-app', target: 'build' };
      const extraTargetDependencies = {
        build: [{ target: 'test', projects: 'self' }]
      };
      const result = inspector.inspectTask(target, {}, extraTargetDependencies);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should handle excludeTaskDependencies parameter', () => {
      const target = { project: 'test-app', target: 'build' };
      const result = inspector.inspectTask(target, {}, {}, true);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should inspect test target', () => {
      const target = { project: 'test-app', target: 'test' };
      const result = inspector.inspectTask(target);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should inspect library project task', () => {
      const target = { project: 'test-lib', target: 'build' };
      const result = inspector.inspectTask(target);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should handle complex parsed args with configuration override', () => {
      const target = { project: 'test-app', target: 'build' };
      const parsedArgs = { 
        configuration: 'development',
        targets: ['build'],
        parallel: 3,
        maxParallel: 3
      };
      const result = inspector.inspectTask(target, parsedArgs);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });
  });

  describe('integration scenarios', () => {
    beforeEach(async () => {
      await inspector.init();
    });

    it('should handle both inspectHashPlan and inspectTask on same instance', () => {
      const hashPlanResult = inspector.inspectHashPlan(['test-app'], ['build']);
      const taskResult = inspector.inspectTask({ project: 'test-app', target: 'build' });

      expect(hashPlanResult).toBeDefined();
      expect(taskResult).toBeDefined();
      expect(typeof hashPlanResult).toBe('object');
      expect(typeof taskResult).toBe('object');
    });

    it('should work with project dependencies', () => {
      // test-app depends on test-lib, so building test-app should include test-lib
      const result = inspector.inspectHashPlan(['test-app'], ['build']);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should handle non-existent project gracefully', () => {
      expect(() => {
        inspector.inspectHashPlan(['non-existent-project'], ['build']);
      }).not.toThrow();
    });

    it('should handle non-existent target gracefully', () => {
      expect(() => {
        inspector.inspectHashPlan(['test-app'], ['non-existent-target']);
      }).not.toThrow();
    });
  });
});

