      const result = inspector.inspectHashPlan(
        ['test-app'],
        ['build'],
        undefined,
        overrides
      );

      expect(result).toBeDefined();
      expect(result['test-app:build']).toBeDefined();
      expect(Array.isArray(result['test-app:build'])).toBe(true);
    });

