use std::env;
use std::process::Command;

/// Detects if the current process is being run by an AI agent like Claude, Cursor, or Codex
pub fn is_ai_agent_detected() -> bool {
    check_environment_variables() 
        || check_process_info()
        || check_parent_process()
        || check_common_ai_indicators()
}

/// Check for AI-related environment variables
fn check_environment_variables() -> bool {
    let ai_env_vars = [
        "ANTHROPIC_API_KEY",
        "OPENAI_API_KEY", 
        "CLAUDE_CODE",
        "CURSOR_SESSION",
        "CODEX_SESSION",
        "AI_ASSISTANT",
        "COPILOT_SESSION",
        "GITHUB_COPILOT",
        "VSCODE_CWD", // VSCode with Copilot
        "CURSOR_USER_ID",
        "ANTHROPIC_MODEL",
    ];

    ai_env_vars.iter().any(|var| env::var(var).is_ok())
}

/// Check process name and command line arguments
fn check_process_info() -> bool {
    // Check current process name
    if let Ok(current_exe) = env::current_exe() {
        if let Some(exe_name) = current_exe.file_name() {
            let exe_str = exe_name.to_string_lossy().to_lowercase();
            if ai_process_names().iter().any(|name| exe_str.contains(name)) {
                return true;
            }
        }
    }

    // Check command line arguments
    let args: Vec<String> = env::args().collect();
    let args_string = args.join(" ").to_lowercase();

    ai_keywords().iter().any(|keyword| args_string.contains(keyword))
}

/// Check parent process information
fn check_parent_process() -> bool {
    #[cfg(unix)]
    {
        if let Ok(output) = Command::new("ps")
            .args(["-o", "comm=", "-p", &env::var("PPID").unwrap_or_default()])
            .output()
        {
            let parent_name = String::from_utf8_lossy(&output.stdout).to_lowercase();
            return ai_process_names().iter().any(|name| parent_name.contains(name));
        }
    }

    #[cfg(windows)]
    {
        if let Ok(output) = Command::new("wmic")
            .args(["process", "where", &format!("ProcessId={}", 
                env::var("PPID").unwrap_or_default()), "get", "Name"])
            .output()
        {
            let parent_name = String::from_utf8_lossy(&output.stdout).to_lowercase();
            return ai_process_names().iter().any(|name| parent_name.contains(name));
        }
    }

    false
}

/// Check for common AI agent indicators
fn check_common_ai_indicators() -> bool {
    // Check for specific user agent patterns or session identifiers
    env::var("USER_AGENT")
        .unwrap_or_default()
        .to_lowercase()
        .contains("ai-agent")
    || env::var("HTTP_USER_AGENT")
        .unwrap_or_default()
        .to_lowercase()
        .contains("claude")
    || env::var("SESSION_TYPE")
        .unwrap_or_default()
        .to_lowercase()
        .contains("ai")
}

/// List of AI-related process names to check for
fn ai_process_names() -> Vec<&'static str> {
    vec![
        "claude",
        "cursor",
        "codex", 
        "copilot",
        "anthropic",
        "openai",
        "code", // VSCode
        "cursor.exe",
        "claude-code",
        "ai-assistant",
    ]
}

/// List of AI-related keywords to check for in command line
fn ai_keywords() -> Vec<&'static str> {
    vec![
        "anthropic",
        "claude",
        "cursor", 
        "codex",
        "copilot",
        "ai-agent",
        "openai",
        "gpt",
        "assistant",
    ]
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_ai_detection_with_env_var() {
        env::set_var("CLAUDE_CODE", "true");
        assert!(is_ai_agent_detected());
        env::remove_var("CLAUDE_CODE");
    }

    #[test]
    fn test_no_ai_detection_clean_env() {
        // Remove any AI-related env vars for this test
        let ai_vars = ["ANTHROPIC_API_KEY", "OPENAI_API_KEY", "CLAUDE_CODE"];
        for var in &ai_vars {
            env::remove_var(var);
        }

        // This might still detect AI if running in an AI environment
        // so we just test that the function runs without panicking
        let _result = is_ai_agent_detected();
    }
}

// Example usage
#[allow(dead_code)]
fn main() {
    if is_ai_agent_detected() {
        println!("ðŸ¤– AI agent detected! Running in an AI environment.");
    } else {
        println!("ðŸ‘¤ Running in a regular user environment.");
    }
}

