  1	#!/usr/bin/env node
  2	
  3	/**
  4	 * Test script for Hash Plan Inspector
  5	 *
  6	 * This script demonstrates how to use the new Hash Plan Inspector
  7	 * to analyze how Nx calculates task hashes for debugging and optimization.
  8	 */
  9	
 10	import { createProjectGraphAsync } from './build/packages/nx/src/project-graph/project-graph';
 11	import { HashPlanInspector } from './build/packages/nx/src/hasher/hash-plan-inspector';
 12	import { readNxJson } from './build/packages/nx/src/config/nx-json.ts';
 13	
 14	async function runHashInspectorTests() {
 15	  console.log(' Testing Hash Plan Inspector...\n');
 16	
 17	  try {
 18	    // Step 1: Create project graph
 19	    console.log(' Creating project graph...');
 20	    const projectGraph = await createProjectGraphAsync();
 21	    console.log(
 22	      ` Project graph loaded with ${
 23	        Object.keys(projectGraph.nodes).length
 24	      } projects\n`
 25	    );
 26	
 27	    // Step 2: Initialize Hash Plan Inspector
 28	    console.log(' Initializing Hash Plan Inspector...');
 29	    const nxJson = readNxJson();
 30	    const inspector = new HashPlanInspector(nxJson, projectGraph);
 31	    await inspector.init();
 32	    console.log(' Hash Plan Inspector initialized\n');
 33	
 34	    // Step 3: Test with different project targets
 35	    const testTargets = [
 36	      // Core nx package
 37	      { project: 'nx', target: 'build', configuration: undefined },
 38	      { project: 'nx', target: 'test', configuration: undefined },
 39	      { project: 'nx', target: 'lint', configuration: undefined },
 40	
 41	      // Try a few other packages
 42	      { project: 'devkit', target: 'build', configuration: undefined },
 43	      { project: 'jest', target: 'test', configuration: undefined },
 44	      { project: 'react', target: 'lint', configuration: undefined },
 45	    ];
 46	
 47	    for (const target of testTargets) {
 48	      try {
 49	        console.log(
 50	          ` Inspecting hash plan for ${target.project}:${target.target}${
 51	            target.configuration ? ':' + target.configuration : ''
 52	          }`
 53	        );
 54	
 55	        const hashPlan = inspector.inspectHashPlan(target);
 56	
 57	        if (hashPlan && Object.keys(hashPlan).length > 0) {
 58	          console.log(
 59	            ` Hash plan generated with ${
 60	              Object.keys(hashPlan).length
 61	            } entries:`
 62	          );
 63	
 64	          // Show the complete hash plan with all files
 65	          for (const [planKey, files] of Object.entries(hashPlan)) {
 66	            console.log(`  - ${planKey}: ${files.length} files`);
 67	
 68	            // Show ALL files in the hash plan
 69	            if (files.length > 0) {
 70	              files.forEach((file) => console.log(`     ${file}`));
 71	            }
 72	          }
 73	        } else {
 74	          console.log('  No hash plan data returned');
 75	        }
 76	
 77	        console.log(''); // Empty line for spacing
