 2	  HashPlanInspector as NativeHashPlanInspector,
 3	  HashPlanner,
 4	  transferProjectGraph,
 5	  ExternalObject,
 6	  ProjectGraph as NativeProjectGraph,
 7	} from '../native';
 8	import { readNxJson } from '../config/nx-json';
 9	import { transformProjectGraphForRust } from '../native/transform-objects';
10	import { ProjectGraph } from '../config/project-graph';
11	import { workspaceRoot } from '../utils/workspace-root';
12	import { createProjectRootMappings } from '../project-graph/utils/find-project-for-path';
13	import { createTaskGraph } from '../tasks-runner/create-task-graph';
14	import type { Target } from '../command-line/run/run';
15	import { getNxWorkspaceFilesFromContext } from '../utils/workspace-context';
16	
17	export class HashPlanInspector {
18	  private readonly projectGraphRef: ExternalObject<NativeProjectGraph>;
19	  private planner: HashPlanner;
20	  private inspector: NativeHashPlanInspector;
21	
22	  constructor(
23	    private readonly nxJson = readNxJson(),
24	    private projectGraph: ProjectGraph
25	  ) {
26	    this.projectGraphRef = transferProjectGraph(
27	      transformProjectGraphForRust(this.projectGraph)
28	    );
29	    this.planner = new HashPlanner(this.nxJson, this.projectGraphRef);
30	  }
31	
32	  async init() {
33	    const projectRootMap = createProjectRootMappings(this.projectGraph.nodes);
34	    const map = Object.fromEntries(projectRootMap.entries());
35	    const { externalReferences } = await getNxWorkspaceFilesFromContext(
36	      workspaceRoot,
37	      map,
38	      false
39	    );
40	    this.inspector = new NativeHashPlanInspector(
41	      externalReferences.allWorkspaceFiles,
42	      this.projectGraphRef,
