const path = require('path');

async function testHashInspector() {
  console.log(' Simple Hash Inspector Test\n');

  try {
    // First check if the native module exists
    const builtNxPath = path.join(__dirname, 'build/packages/nx');
    console.log(' Checking for built nx package at:', builtNxPath);

    const fs = require('fs');
    if (!fs.existsSync(builtNxPath)) {
      console.log(' Built nx package not found. Run: nx build nx');
      return;
    }

    console.log(' Built nx package found');

    // Try to require the built modules
    const {
      createProjectGraphAsync,
    } = require('./build/packages/nx/src/project-graph/project-graph');
    const {
      HashPlanInspector,
    } = require('./build/packages/nx/src/hasher/hash-plan-inspector');
    const { readNxJson } = require('./build/packages/nx/src/config/nx-json');

    console.log(' Modules loaded successfully');

    // Basic functionality test
    console.log('\n Testing basic functionality...');

    const projectGraph = await createProjectGraphAsync();
    console.log(
      ` Project graph loaded with ${
        Object.keys(projectGraph.nodes).length
      } projects`
    );

    const nxJson = readNxJson();
    console.log(' nx.json loaded');

    const inspector = new HashPlanInspector(projectGraph, nxJson);
    console.log(' HashPlanInspector created');

    await inspector.init();
    console.log(' HashPlanInspector initialized');

    // Test with a simple target
    const target = { project: 'nx', target: 'build', configuration: undefined };
    console.log(
      `\n Testing hash plan for ${target.project}:${target.target}`
    );

    const hashPlan = inspector.inspectHashPlan(target);

    if (hashPlan && Object.keys(hashPlan).length > 0) {
      console.log(' Hash plan generated successfully!');
      console.log(` Contains ${Object.keys(hashPlan).length} plan entries:`);

      for (const [planKey, files] of Object.entries(hashPlan)) {
        console.log(`   ${planKey}: ${files.length} files`);

        // Show first few files as examples
        if (files.length > 0) {
          files.forEach((file) => console.log(`     ${file}`));
        }
      }
    } else {
      console.log('  No hash plan returned');
    }

    console.log('\n Test completed successfully!');
  } catch (error) {
    console.error(' Error:', error.message);
    if (error.code === 'MODULE_NOT_FOUND') {
      console.log(
        '\n Tip: Make sure to run "nx build nx" first to build the native components'
      );
    }
  }
}

testHashInspector().then(() => {
  console.log('done');
  process.exit(0);
});

