import {
  HashPlanInspector as NativeHashPlanInspector,
  HashPlanner,
  transferProjectGraph,
  ExternalObject,
  ProjectGraph as NativeProjectGraph,
} from '../native';
import { readNxJson, NxJsonConfiguration } from '../config/nx-json';
import { transformProjectGraphForRust } from '../native/transform-objects';
import { ProjectGraph } from '../config/project-graph';
import { workspaceRoot } from '../utils/workspace-root';
import { createProjectRootMappings } from '../project-graph/utils/find-project-for-path';
import { createTaskGraph } from '../tasks-runner/create-task-graph';
import type { Target } from '../command-line/run/run';
import { TargetDependencies } from '../config/nx-json';
import { getNxWorkspaceFilesFromContext } from '../utils/workspace-context';

export class HashPlanInspector {
  private readonly projectGraphRef: ExternalObject<NativeProjectGraph>;
  private planner: HashPlanner;
  private inspector: NativeHashPlanInspector;
  private readonly nxJson: NxJsonConfiguration;

  constructor(
    private projectGraph: ProjectGraph,
    private readonly workspaceRootPath: string = workspaceRoot,
    nxJson?: NxJsonConfiguration
  ) {
    this.nxJson = nxJson ?? readNxJson(this.workspaceRootPath);
    this.projectGraphRef = transferProjectGraph(
      transformProjectGraphForRust(this.projectGraph)
    );
    this.planner = new HashPlanner(this.nxJson, this.projectGraphRef);
  }

  async init() {
    const projectRootMap = createProjectRootMappings(this.projectGraph.nodes);
    const map = Object.fromEntries(projectRootMap.entries());
    const { externalReferences } = await getNxWorkspaceFilesFromContext(
      this.workspaceRootPath,
      map,
      false
    );
    this.inspector = new NativeHashPlanInspector(
      externalReferences.allWorkspaceFiles,
      this.projectGraphRef,
      externalReferences.projectFiles
    );
  }

  inspectHashPlan(
    projectNames: string[],
    targets: string[],
    configuration?: string,
    overrides: Object = {},
    extraTargetDependencies: TargetDependencies = {},
    excludeTaskDependencies: boolean = false
  ) {
    const taskGraph = createTaskGraph(
      this.projectGraph,
      extraTargetDependencies,
      projectNames,
      targets,
      configuration,
      overrides,
      excludeTaskDependencies
    );
    // Generate task IDs for all combinations of projects and targets
    const taskIds: string[] = [];
    for (const project of projectNames) {
      for (const target of targets) {
        const taskId = `${project}:${target.indexOf(':') > -1 ? `"${target}"` : target}${
          configuration !== undefined ? ':' + configuration : ''
        }`;
        taskIds.push(taskId);
      }
    }

    const plansReference = this.planner.getPlansReference(taskIds, taskGraph);

    return this.inspector.inspect(plansReference);
  }
}

#!/usr/bin/env node

/**
 * Hash Plan Inspector Test Suite
 *
 * This script demonstrates how to use the Hash Plan Inspector to analyze
 * how Nx calculates task hashes for debugging and optimization.
 *
 * Usage: node test-hash-inspector.js
 * Prerequisites: nx build nx (to build the native components)
 * Note: Initial run may take a minute due to project graph initialization
 */

const {
  createProjectGraphAsync,
} = require('./build/packages/nx/src/project-graph/project-graph');
const {
  HashPlanInspector,
} = require('./build/packages/nx/src/hasher/hash-plan-inspector');

async function runHashInspectorTests() {
  console.log(' Testing Hash Plan Inspector...\n');

  try {
    // Step 0: Check if built nx package exists
    const fs = require('fs');
    const builtNxPath = './build/packages/nx';
    if (!fs.existsSync(builtNxPath)) {
      console.log(' Built nx package not found. Run: nx build nx');
      return;
    }
    console.log(' Built nx package found\n');

    // Step 1: Create project graph
    console.log(' Creating project graph...');
    const projectGraph = await createProjectGraphAsync();
    console.log(
      ` Project graph loaded with ${
        Object.keys(projectGraph.nodes).length
      } projects\n`
    );

    // Step 2: Initialize Hash Plan Inspector
    console.log(' Initializing Hash Plan Inspector...');
    const inspector = new HashPlanInspector(projectGraph);
    await inspector.init();
    console.log(' Hash Plan Inspector initialized\n');

    // Step 3: Test with different project targets (limited for performance)
    const testTargets = [
      // Core nx package
      {
        project: 'nx',
        target: 'build',
        configuration: undefined,
      },
      // Add more targets as needed...
    ];

    for (const target of testTargets) {
      try {
        console.log(
          ` Inspecting hash plan for ${target.project}:${target.target}${
            target.configuration ? ':' + target.configuration : ''
          }`
        );

        const hashPlan = inspector.inspectHashPlan(target);

        require('fs').writeFileSync(
          'plan.json',
          JSON.stringify(hashPlan, null, 2)
        );

        if (hashPlan && Object.keys(hashPlan).length > 0) {
          console.log(
            ` Hash plan generated with ${
              Object.keys(hashPlan).length
            } entries:`
          );

          // Show the hash plan summary with sample files
          for (const [planKey, files] of Object.entries(hashPlan)) {
            console.log(`  - ${planKey}: ${files.length} files`);

            // Show first few files as examples
            if (files.length > 0) {
              const samplesToShow = Math.min(5, files.length);
              files
                .slice(0, samplesToShow)
                .forEach((file) => console.log(`     ${file}`));
              if (files.length > samplesToShow) {
                console.log(
                  `    ... and ${files.length - samplesToShow} more files`
                );
              }
            }
          }
        } else {
          console.log('  No hash plan data returned');
        }

        console.log(''); // Empty line for spacing
      } catch (error) {
        console.error(
          ` Error inspecting ${target.project}:${target.target} - ${error.message}`
        );
        console.log(''); // Empty line for spacing
      }
    }

    // Step 4: Test with non-existent target to see error handling
    console.log(' Testing error handling with non-existent target...');
    try {
      const badTarget = {
        project: 'nonexistent',
        target: 'build',
        configuration: undefined,
      };
      inspector.inspectHashPlan(badTarget);
      console.log('  Expected error but none occurred');
    } catch (error) {
      console.log(` Error handling works: ${error.message}`);
    }
  } catch (error) {
    console.error(' Fatal error:', error);
    console.error('Stack trace:', error.stack);
    process.exit(1);
  }
}

// Performance test function
async function performanceTest() {
  console.log('\n Running performance test...');

  const projectGraph = await createProjectGraphAsync();
  const inspector = new HashPlanInspector(projectGraph);
  await inspector.init();

  const target = { project: 'nx', target: 'build', configuration: undefined };

  const runs = 3;
  const times = [];

  for (let i = 0; i < runs; i++) {
    const start = process.hrtime.bigint();
    inspector.inspectHashPlan(target);
    const end = process.hrtime.bigint();

    const duration = Number(end - start) / 1000000; // Convert to milliseconds
    times.push(duration);
  }

  const avgTime = times.reduce((a, b) => a + b, 0) / times.length;
  const minTime = Math.min(...times);
  const maxTime = Math.max(...times);

  console.log(` Performance Results (${runs} runs):`);
  console.log(`  Average: ${avgTime.toFixed(2)}ms`);
  console.log(`  Min: ${minTime.toFixed(2)}ms`);
  console.log(`  Max: ${maxTime.toFixed(2)}ms`);
}

// Run the tests
if (require.main === module) {
  console.log(' Hash Plan Inspector Test Suite');
  console.log('==================================\n');

  runHashInspectorTests()
    .then(() => performanceTest())
    .then(() => {
      console.log('\n All tests completed successfully!');
      console.log('\n The Hash Plan Inspector helps you understand:');
      console.log('    Which files affect task hashing');
      console.log('    How changes impact cache invalidation');
      console.log('    Debug performance issues with large file sets');
      console.log('    Optimize your workspace for better caching');
    })
    .catch((error) => {
      console.error('\n Test suite failed:', error);
      process.exit(1);
    });
}

