#!/usr/bin/env node

/**
 * Hash Plan Inspector Test Suite
 *
 * This script demonstrates the flexible Hash Plan Inspector API including:
 * - Single project/target analysis
 * - Multi-project analysis
 * - Multi-target analysis  
 * - Target object convenience method
 * - Target string parsing (like run-one.ts)
 * - Performance testing
 *
 * Usage: node test-hash-inspector.js
 * Prerequisites: nx build nx (to build the native components)
 * Note: Initial run may take a minute due to project graph initialization
 */

const {
  createProjectGraphAsync,
} = require('./build/packages/nx/src/project-graph/project-graph');
const {
  HashPlanInspector,
} = require('./build/packages/nx/src/hasher/hash-plan-inspector');

async function runHashInspectorTests() {
  console.log(' Testing Hash Plan Inspector...\n');

  try {
    // Step 0: Check if built nx package exists
    const fs = require('fs');
    const builtNxPath = './build/packages/nx';
    if (!fs.existsSync(builtNxPath)) {
      console.log(' Built nx package not found. Run: nx build nx');
      return;
    }
    console.log(' Built nx package found\n');

    // Step 1: Create project graph
    console.log(' Creating project graph...');
    const projectGraph = await createProjectGraphAsync();
    console.log(
      ` Project graph loaded with ${
        Object.keys(projectGraph.nodes).length
      } projects\n`
    );

    // Step 2: Initialize Hash Plan Inspector
    console.log(' Initializing Hash Plan Inspector...');
    const inspector = new HashPlanInspector(projectGraph);
    await inspector.init();
    console.log(' Hash Plan Inspector initialized\n');

    // Step 3: Test the flexible inspectHashPlan API

    // Test 1: Single project, single target
    console.log(' Test 1: Single project, single target');
    const singlePlan = inspector.inspectHashPlan(['nx'], ['build']);
    console.log(` Single plan: ${Object.keys(singlePlan).length} entries`);
    for (const [planKey, files] of Object.entries(singlePlan)) {
      console.log(`  - ${planKey}: ${files.length} files`);
      if (files.length > 0) {
        const samplesToShow = Math.min(3, files.length);
        files.slice(0, samplesToShow).forEach((file) => console.log(`     ${file}`));
        if (files.length > samplesToShow) {
          console.log(`    ... and ${files.length - samplesToShow} more files`);
        }
      }
    }
    console.log('');

    // Test 2: Multiple projects, single target
    console.log(' Test 2: Multiple projects, same target');
    const multiProjectPlan = inspector.inspectHashPlan(['nx', 'devkit'], ['build']);
    console.log(` Multi-project plan: ${Object.keys(multiProjectPlan).length} entries`);
    for (const [planKey, files] of Object.entries(multiProjectPlan)) {
      console.log(`  - ${planKey}: ${files.length} files (showing samples)`);
      if (files.length > 0) {
        files.slice(0, 2).forEach((file) => console.log(`     ${file}`));
        if (files.length > 2) {
          console.log(`    ... and ${files.length - 2} more files`);
        }
      }
    }
    console.log('');

    // Test 3: Single project, multiple targets  
    console.log(' Test 3: Single project, multiple targets');
    const multiTargetPlan = inspector.inspectHashPlan(['nx'], ['build', 'test']);
    console.log(` Multi-target plan: ${Object.keys(multiTargetPlan).length} entries`);
    for (const [planKey, files] of Object.entries(multiTargetPlan)) {
      console.log(`  - ${planKey}: ${files.length} files (summary only)`);
    }
    console.log('');

    // Test 4: Target object convenience method
    console.log(' Test 4: Target object convenience method');
    const target = { project: 'nx', target: 'build', configuration: undefined };
    const targetPlan = inspector.inspectTarget(target);
    console.log(` Target plan: ${Object.keys(targetPlan).length} entries`);
    for (const [planKey, files] of Object.entries(targetPlan)) {
      console.log(`  - ${planKey}: ${files.length} files`);
    }
    console.log('');

    // Test 5: Target string convenience method (like run-one.ts)
    console.log(' Test 5: Target string parsing (like run-one.ts)');
    const stringPlan = inspector.inspectTargetString('nx:build');
    console.log(` String plan: ${Object.keys(stringPlan).length} entries`);
    for (const [planKey, files] of Object.entries(stringPlan)) {
      console.log(`  - ${planKey}: ${files.length} files`);
    }
    console.log('');

    // Write one plan to file for inspection
    require('fs').writeFileSync(
      'plan.json',
      JSON.stringify(targetPlan, null, 2)
    );
    console.log(' Sample plan written to plan.json');
    console.log('');

    // Test 6: Error handling with non-existent target
    console.log(' Testing error handling with non-existent target...');
    try {
      inspector.inspectHashPlan(['nonexistent'], ['build']);
      console.log('  Expected error but none occurred');
    } catch (error) {
      console.log(` Error handling works: ${error.message}`);
    }
  } catch (error) {
    console.error(' Fatal error:', error);
    console.error('Stack trace:', error.stack);
    process.exit(1);
  }
}

// Performance test function
async function performanceTest() {
  console.log('\n Running performance test...');

  const projectGraph = await createProjectGraphAsync();
  const inspector = new HashPlanInspector(projectGraph);
  await inspector.init();

  const runs = 3;
  const times = [];

  for (let i = 0; i < runs; i++) {
    const start = process.hrtime.bigint();
    inspector.inspectHashPlan(['nx'], ['build']);
    const end = process.hrtime.bigint();

    const duration = Number(end - start) / 1000000; // Convert to milliseconds
    times.push(duration);
  }

  const avgTime = times.reduce((a, b) => a + b, 0) / times.length;
  const minTime = Math.min(...times);
  const maxTime = Math.max(...times);

  console.log(` Performance Results (${runs} runs):`);
  console.log(`  Average: ${avgTime.toFixed(2)}ms`);
  console.log(`  Min: ${minTime.toFixed(2)}ms`);
  console.log(`  Max: ${maxTime.toFixed(2)}ms`);
}

// Run the tests
if (require.main === module) {
  console.log(' Hash Plan Inspector Test Suite');
  console.log('==================================\n');

  runHashInspectorTests()
    .then(() => performanceTest())
    .then(() => {
      console.log('\n All tests completed successfully!');
      console.log('\n The Hash Plan Inspector helps you understand:');
      console.log('    Which files affect task hashing');
      console.log('    How changes impact cache invalidation');
      console.log('    Debug performance issues with large file sets');
      console.log('    Optimize your workspace for better caching');
    })
    .catch((error) => {
      console.error('\n Test suite failed:', error);
      process.exit(1);
    });
}

