    // Step 2: Initialize Hash Plan Inspector
    console.log(' Initializing Hash Plan Inspector...');
    const inspector = new HashPlanInspector(projectGraph);
    await inspector.init();
    console.log(' Hash Plan Inspector initialized\n');

    // Step 3: Test the flexible inspectHashPlan API

    // Test 1: Single project, single target
    console.log(' Test 1: Single project, single target');
    const singlePlan = inspector.inspectHashPlan(['nx'], ['build']);
    console.log(` Single plan: ${Object.keys(singlePlan).length} entries`);
    for (const [planKey, files] of Object.entries(singlePlan)) {
