#!/usr/bin/env node

/**
 * Hash Plan Inspector Test Suite
 *
 * This script demonstrates the flexible Hash Plan Inspector API including:
 * - Single project/target analysis
 * - Multi-project analysis
 * - Multi-target analysis
 * - Exact run-one.ts behavior mirroring
 * - Performance testing
 *
 * Usage: node test-hash-inspector.js
 * Prerequisites: nx build nx (to build the native components)
 * Note: Initial run may take a minute due to project graph initialization
 */

const {
  createProjectGraphAsync,
} = require('./build/packages/nx/src/project-graph/project-graph');
const {
  HashPlanInspector,
} = require('./build/packages/nx/src/hasher/hash-plan-inspector');

async function runHashInspectorTests() {
  console.log(' Testing Hash Plan Inspector...\n');

  try {
    // Step 0: Check if built nx package exists
    const fs = require('fs');
    const builtNxPath = './build/packages/nx';
    if (!fs.existsSync(builtNxPath)) {
      console.log(' Built nx package not found. Run: nx build nx');
      return;
    }
    console.log(' Built nx package found\n');

    // Step 1: Create project graph
    console.log(' Creating project graph...');
    const projectGraph = await createProjectGraphAsync();
    console.log(
      ` Project graph loaded with ${
        Object.keys(projectGraph.nodes).length
      } projects\n`
    );

    // Step 2: Initialize Hash Plan Inspector
    console.log(' Initializing Hash Plan Inspector...');
    const inspector = new HashPlanInspector(projectGraph);
    await inspector.init();
    console.log(' Hash Plan Inspector initialized\n');

    // Step 3: Test the flexible inspectHashPlan API

    // Test 1: Single project, single target
    console.log(' Test 1: Single project, single target');
    const singlePlan = inspector.inspectHashPlan(['nx'], ['build']);
    console.log(` Single plan: ${Object.keys(singlePlan).length} entries`);
    for (const [planKey, files] of Object.entries(singlePlan)) {
      console.log(`  - ${planKey}: ${files.length} files`);
      if (files.length > 0) {
        const samplesToShow = Math.min(3, files.length);
        files
          .slice(0, samplesToShow)
          .forEach((file) => console.log(`     ${file}`));
        if (files.length > samplesToShow) {
          console.log(`    ... and ${files.length - samplesToShow} more files`);
        }
      }
    }
    console.log('');

    // Test 2: Multiple projects, single target
    console.log(' Test 2: Multiple projects, same target');
    const multiProjectPlan = inspector.inspectHashPlan(
      ['nx', 'devkit'],
      ['build']
    );
    console.log(
      ` Multi-project plan: ${Object.keys(multiProjectPlan).length} entries`
    );
    for (const [planKey, files] of Object.entries(multiProjectPlan)) {
      console.log(`  - ${planKey}: ${files.length} files (showing samples)`);
      if (files.length > 0) {
        files.slice(0, 2).forEach((file) => console.log(`     ${file}`));
        if (files.length > 2) {
          console.log(`    ... and ${files.length - 2} more files`);
        }
      }
    }
    console.log('');

    // Test 3: Single project, multiple targets
    console.log(' Test 3: Single project, multiple targets');
    const multiTargetPlan = inspector.inspectHashPlan(
      ['nx'],
      ['build', 'test']
    );
    console.log(
      ` Multi-target plan: ${Object.keys(multiTargetPlan).length} entries`
    );
    for (const [planKey, files] of Object.entries(multiTargetPlan)) {
      console.log(`  - ${planKey}: ${files.length} files (summary only)`);
    }
    console.log('');

    // Test 4: Single target using main API
    console.log(' Test 4: Single target using main API');
    const singleTargetPlan = inspector.inspectHashPlan(['nx'], ['build']);
    console.log(` Single target plan: ${Object.keys(singleTargetPlan).length} entries`);
    for (const [planKey, files] of Object.entries(singleTargetPlan)) {
      console.log(`  - ${planKey}: ${files.length} files`);
    }
    console.log('');

    // Write one plan to file for inspection
    require('fs').writeFileSync(
      'plan.json',
      JSON.stringify(singleTargetPlan, null, 2)
    );
    console.log(' Sample plan written to plan.json');
    console.log('');

    // Test 5: Mirror run-one.ts behavior exactly
    console.log(' Test 5: Mirror run-one.ts behavior exactly');
    const runOnePlan = inspector.inspectLikeRunOne(
      { project: 'nx', target: 'build', configuration: undefined },
      { verbose: true }, // parsedArgs like CLI would provide
      {} // extraTargetDependencies
    );
    console.log(
      ` Run-one style plan: ${Object.keys(runOnePlan).length} entries`
    );
    for (const [planKey, files] of Object.entries(runOnePlan)) {
      console.log(`  - ${planKey}: ${files.length} files`);
    }
    console.log('');

    require('fs').writeFileSync(
      'plan.json',
      JSON.stringify(runOnePlan, null, 2)
    );

    // Test 6: Error handling with non-existent target
    console.log(' Testing error handling with non-existent target...');
    try {
      inspector.inspectHashPlan(['nonexistent'], ['build']);
      console.log('  Expected error but none occurred');
    } catch (error) {
      console.log(` Error handling works: ${error.message}`);
    }
  } catch (error) {
    console.error(' Fatal error:', error);
    console.error('Stack trace:', error.stack);
    process.exit(1);
  }
}

// Performance test function
async function performanceTest() {
  console.log('\n Running performance test...');

  const projectGraph = await createProjectGraphAsync();
  const inspector = new HashPlanInspector(projectGraph);
  await inspector.init();

  const runs = 3;
  const times = [];

  for (let i = 0; i < runs; i++) {
    const start = process.hrtime.bigint();
    inspector.inspectHashPlan(['nx'], ['build']);
    const end = process.hrtime.bigint();

    const duration = Number(end - start) / 1000000; // Convert to milliseconds
    times.push(duration);
  }

  const avgTime = times.reduce((a, b) => a + b, 0) / times.length;
  const minTime = Math.min(...times);
  const maxTime = Math.max(...times);

  console.log(` Performance Results (${runs} runs):`);
  console.log(`  Average: ${avgTime.toFixed(2)}ms`);
  console.log(`  Min: ${minTime.toFixed(2)}ms`);
  console.log(`  Max: ${maxTime.toFixed(2)}ms`);
}

// Run the tests
if (require.main === module) {
  console.log(' Hash Plan Inspector Test Suite');
  console.log('==================================\n');

  runHashInspectorTests()
    .then(() => performanceTest())
    .then(() => {
      console.log('\n All tests completed successfully!');
      console.log('\n The Hash Plan Inspector helps you understand:');
      console.log('    Which files affect task hashing');
      console.log('    How changes impact cache invalidation');
      console.log('    Debug performance issues with large file sets');
      console.log('    Optimize your workspace for better caching');
    })
    .catch((error) => {
      console.error('\n Test suite failed:', error);
      process.exit(1);
    });
}

# Hash Plan Inspector

The Hash Plan Inspector is a debugging tool that reveals how Nx calculates task hashes. Think of it like an X-ray for your build system - it shows you exactly which files and inputs affect the hash calculation for tasks and their dependencies in the entire task graph.

## What It Does

When Nx decides whether to use cached results or run a task fresh, it creates a hash based on:
- Source files
- Dependencies 
- Configuration
- Environment variables

The Hash Plan Inspector breaks down this process, showing you exactly which files contribute to each task's hash across the entire dependency graph - not just the target task, but all its dependencies too.

## Why Use It

- **Debug cache misses**: Understand why your tasks aren't hitting the cache
- **Optimize performance**: See if too many files are being scanned unnecessarily
- **Workspace analysis**: Understand how projects depend on each other
- **Fine-tune caching**: Optimize file patterns for better cache hit rates

## Basic Usage

```typescript
import { createProjectGraphAsync } from '@nx/nx/src/project-graph/project-graph';
import { HashPlanInspector } from '@nx/nx/src/hasher/hash-plan-inspector';
import { readNxJson } from '@nx/nx/src/config/nx-json';

async function inspectTask() {
  // 1. Set up the inspector
  const projectGraph = await createProjectGraphAsync();
  const inspector = new HashPlanInspector(projectGraph); // workspaceRoot and nxJson are optional
  await inspector.init();

  // 2. Inspect tasks

  // Option A: Single project/target
  const hashPlan = inspector.inspectHashPlan(['my-app'], ['build'], 'production');

  // Option B: Multiple projects/targets
  const hashPlan2 = inspector.inspectHashPlan(
    ['my-app', 'my-lib'], // projects
    ['build', 'test'],    // targets
    'production'          // configuration
  );

  // Option C: Exact run-one.ts behavior (with CLI args processing)
  const hashPlan3 = inspector.inspectLikeRunOne(
    { project: 'my-app', target: 'build', configuration: 'production' },
    { verbose: true }, // CLI args
    { build: ['^build'] } // target dependencies
  );

  // 3. Analyze the results
  console.log('Files affecting this task:');
  for (const [planKey, files] of Object.entries(hashPlan)) {
    console.log(`${planKey}: ${files.length} files`);
    files.forEach(file => console.log(`  - ${file}`));
  }
}
```

## What the Output Tells You

The inspector returns an object where:
- **Keys** represent different aspects of the hash plan (like file patterns or input sources)
- **Values** are arrays of file paths that contribute to the hash

Example output:
```javascript
{
  "my-app:build": [
    "apps/my-app/src/index.ts",
    "apps/my-app/package.json",
    "nx.json"
  ],
  "my-lib:build": [
    "libs/my-lib/src/index.ts", 
    "libs/my-lib/package.json"
  ],
  "shared-lib:build": [
    "libs/shared/src/index.ts",
    "libs/shared/package.json"
  ]
}
```

Notice how it shows hash plans for all tasks in the dependency chain, not just the requested target.

### Why Include All Dependencies?

When you run a task, Nx doesn't just hash your target - it hashes all dependent tasks too. This gives you:

- **Complete dependency insight**: See what files affect the entire build chain
- **Root cause analysis**: Find which dependency is causing cache misses
- **Optimization opportunities**: Identify overly broad file patterns in dependencies
- **Full context**: Understand the complete scope of what affects your build

## Common Use Cases

### 1. Debugging Cache Misses

When a task should hit cache but doesn't:

```typescript
const hashPlan = inspector.inspectHashPlan(['failing-app'], ['build']);

// Look for unexpected files in the hash
console.log('Total files:', Object.values(hashPlan).flat().length);
```

### 2. Performance Analysis

If builds are slow, check if too many files are being scanned:

```typescript
const hashPlan = inspector.inspectHashPlan(['my-app'], ['build']);
const allFiles = Object.values(hashPlan).flat();

if (allFiles.length > 1000) {
  console.log(' Many files detected - consider optimizing inputs');
}
```

### 3. Workspace Optimization

Compare hash plans between similar projects to ensure consistency:

```typescript
const app1Plan = inspector.inspectHashPlan(['app1'], ['build']);
const app2Plan = inspector.inspectHashPlan(['app2'], ['build']);

// Compare file counts to spot inconsistencies

// OR analyze multiple apps at once
const multiAppPlan = inspector.inspectHashPlan(['app1', 'app2'], ['build']);
```

## Test Script

A comprehensive test script demonstrates the functionality:

- `test-hash-inspector.js` - Complete test suite with performance analysis and usage examples

Run it after building the nx package:

```bash
nx build nx
node test-hash-inspector.js
```

## Requirements

- The Nx package must be built (`nx build nx`) 
- Requires both TypeScript and Rust components
- Works with any Nx workspace

## Implementation Notes

- Uses Rust for performance-critical hash calculations
- TypeScript wrapper provides easy-to-use API
- Integrates with Nx's existing project graph and task system
- Minimal overhead - suitable for development and debugging

## Error Handling

The inspector will throw errors for:
- Non-existent projects
- Invalid target configurations
- Missing dependencies

Always wrap calls in try-catch blocks for production use.

