import { splitArgsIntoNxArgsAndOverrides } from '../utils/command-line-utils';
import { getNxWorkspaceFilesFromContext } from '../utils/workspace-context';

// Mock the dependencies
jest.mock('../tasks-runner/create-task-graph');
jest.mock('../utils/command-line-utils');
jest.mock('../utils/workspace-context');
jest.mock('../native', () => ({
  transferProjectGraph: jest.fn().mockReturnValue('mock-graph-ref'),
  HashPlanner: jest.fn().mockImplementation(() => ({
    getPlansReference: jest.fn().mockReturnValue('mock-plans-ref'),
  })),
