    it('should return hash plan for all tasks in dependency graph', () => {
      const target = { project: 'my-app', target: 'build', configuration: undefined };

      const result = inspector.inspectTask(target);

      expect(result).toEqual({
        'my-app:build': ['file1.ts', 'file2.ts'],
        'my-lib:build': ['lib-file1.ts', 'lib-file2.ts'],
      });
    });

    it('should use default values for optional parameters', () => {
      const target = { project: 'my-app', target: 'build', configuration: undefined };

      inspector.inspectTask(target);

      expect(splitArgsIntoNxArgsAndOverrides).toHaveBeenCalledWith(
        {
          configuration: undefined,
          targets: ['build'],
        },
        'run-one',
        { printWarnings: false },
        mockNxJson
      );
    });

    it('should handle configuration parameter correctly', () => {
      const target = { project: 'my-app', target: 'build', configuration: 'development' };

      inspector.inspectTask(target);

      expect(splitArgsIntoNxArgsAndOverrides).toHaveBeenCalledWith(
        expect.objectContaining({
          configuration: 'development',
        }),
        expect.any(String),
        expect.any(Object),
        expect.any(Object)
      );
    });

    it('should merge parsedArgs with target information', () => {
      const target = { project: 'my-app', target: 'build', configuration: 'production' };
      const parsedArgs = { verbose: true, dryRun: true };

      inspector.inspectTask(target, parsedArgs);

      expect(splitArgsIntoNxArgsAndOverrides).toHaveBeenCalledWith(
        {
          verbose: true,
          dryRun: true,
          configuration: 'production',
          targets: ['build'],
        },
        'run-one',
        { printWarnings: false },
        mockNxJson
      );
    });
  });

  describe('integration scenarios', () => {
    beforeEach(async () => {
      await inspector.init();
    });

    it('should handle complex target dependencies', () => {
      const extraTargetDependencies = {
        build: ['^build', { target: 'prebuild', params: 'forward' }],
        test: ['^test'],
      };

      inspector.inspectHashPlan(['my-app'], ['build'], undefined, {}, extraTargetDependencies);

      expect(createTaskGraph).toHaveBeenCalledWith(
        expect.any(Object),
        extraTargetDependencies,
        expect.any(Array),
        expect.any(Array),
        expect.any(String),
        expect.any(Object),
        expect.any(Boolean)
      );
    });

    it('should work with excludeTaskDependencies flag', () => {
      inspector.inspectHashPlan(['my-app'], ['build'], undefined, {}, {}, true);

      expect(createTaskGraph).toHaveBeenCalledWith(
        expect.any(Object),
        expect.any(Object),
        expect.any(Array),
        expect.any(Array),
        expect.any(String),
        expect.any(Object),
        true // excludeTaskDependencies
      );
    });

    it('should handle empty project or target arrays', () => {
      inspector.inspectHashPlan([], ['build']);
      inspector.inspectHashPlan(['my-app'], []);

      expect(createTaskGraph).toHaveBeenCalledTimes(2);
    });
  });
});
