#!/usr/bin/env node

/**
 * Test script for Hash Plan Inspector
 * 
 * This script demonstrates how to use the new Hash Plan Inspector
 * to analyze how Nx calculates task hashes for debugging and optimization.
 */

const { createProjectGraphAsync } = require('./packages/nx/src/project-graph/project-graph');
const { HashPlanInspector } = require('./packages/nx/src/hasher/hash-plan-inspector');
const { readNxJson } = require('./packages/nx/src/config/nx-json');

async function runHashInspectorTests() {
  console.log('🔍 Testing Hash Plan Inspector...\n');

  try {
    // Step 1: Create project graph
    console.log('📊 Creating project graph...');
    const projectGraph = await createProjectGraphAsync();
    console.log(`✅ Project graph loaded with ${Object.keys(projectGraph.nodes).length} projects\n`);

    // Step 2: Initialize Hash Plan Inspector
    console.log('🚀 Initializing Hash Plan Inspector...');
    const nxJson = readNxJson();
    const inspector = new HashPlanInspector(nxJson, projectGraph);
    await inspector.init();
    console.log('✅ Hash Plan Inspector initialized\n');

    // Step 3: Test with different project targets
    const testTargets = [
      // Core nx package
      { project: 'nx', target: 'build', configuration: undefined },
      { project: 'nx', target: 'test', configuration: undefined },
      { project: 'nx', target: 'lint', configuration: undefined },

      // Try a few other packages
      { project: 'devkit', target: 'build', configuration: undefined },
      { project: 'jest', target: 'test', configuration: undefined },
      { project: 'react', target: 'lint', configuration: undefined },
    ];

    for (const target of testTargets) {
      try {
        console.log(`🎯 Inspecting hash plan for ${target.project}:${target.target}${target.configuration ? ':' + target.configuration : ''}`);

        const hashPlan = inspector.inspectHashPlan(target);

        if (hashPlan && Object.keys(hashPlan).length > 0) {
          console.log(`📋 Hash plan generated with ${Object.keys(hashPlan).length} entries:`);

          // Show a summary of what files/inputs affect this task's hash
          for (const [planKey, files] of Object.entries(hashPlan)) {
            console.log(`  - ${planKey}: ${files.length} files`);

            // Show first few files as examples
            if (files.length > 0) {
              const exampleFiles = files.slice(0, 3);
              exampleFiles.forEach(file => console.log(`    • ${file}`));
              if (files.length > 3) {
                console.log(`    ... and ${files.length - 3} more files`);
              }
            }
          }
        } else {
          console.log('⚠️  No hash plan data returned');
        }

        console.log(''); // Empty line for spacing

      } catch (error) {
        console.error(`❌ Error inspecting ${target.project}:${target.target} - ${error.message}`);
        console.log(''); // Empty line for spacing
      }
    }

    // Step 4: Test with non-existent target to see error handling
    console.log('🧪 Testing error handling with non-existent target...');
    try {
      const badTarget = { project: 'nonexistent', target: 'build', configuration: undefined };
      inspector.inspectHashPlan(badTarget);
      console.log('⚠️  Expected error but none occurred');
    } catch (error) {
      console.log(`✅ Error handling works: ${error.message}`);
    }

  } catch (error) {
    console.error('❌ Fatal error:', error);
    console.error('Stack trace:', error.stack);
    process.exit(1);
  }
}

// Helper function to demonstrate what the inspector reveals
function analyzeHashPlan(hashPlan, targetName) {
  console.log(`\n📊 Hash Analysis for ${targetName}:`);
  console.log('=====================================');

  let totalFiles = 0;
  const planTypes = Object.keys(hashPlan);

  planTypes.forEach(planType => {
    const files = hashPlan[planType];
    totalFiles += files.length;

    console.log(`\n${planType}:`);
    console.log(`  📁 ${files.length} files`);

    // Categorize files by type
    const fileTypes = {};
    files.forEach(file => {
      const ext = file.split('.').pop();
      fileTypes[ext] = (fileTypes[ext] || 0) + 1;
    });

    console.log('  📋 File types:');
    Object.entries(fileTypes)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 5) // Top 5 file types
      .forEach(([ext, count]) => {
        console.log(`    ${ext}: ${count}`);
      });
  });

  console.log(`\n🔢 Total files affecting hash: ${totalFiles}`);
  console.log(`🏷️  Plan types: ${planTypes.join(', ')}`);
}

// Performance test function
async function performanceTest() {
  console.log('\n⚡ Running performance test...');

  const projectGraph = await createProjectGraphAsync();
  const nxJson = readNxJson();
  const inspector = new HashPlanInspector(nxJson, projectGraph);
  await inspector.init();

  const target = { project: 'nx', target: 'build', configuration: undefined };

  const runs = 10;
  const times = [];

  for (let i = 0; i < runs; i++) {
    const start = process.hrtime.bigint();
    inspector.inspectHashPlan(target);
    const end = process.hrtime.bigint();

    const duration = Number(end - start) / 1000000; // Convert to milliseconds
    times.push(duration);
  }

  const avgTime = times.reduce((a, b) => a + b, 0) / times.length;
  const minTime = Math.min(...times);
  const maxTime = Math.max(...times);

  console.log(`📊 Performance Results (${runs} runs):`);
  console.log(`  Average: ${avgTime.toFixed(2)}ms`);
  console.log(`  Min: ${minTime.toFixed(2)}ms`);
  console.log(`  Max: ${maxTime.toFixed(2)}ms`);
}

// Run the tests
if (require.main === module) {
  console.log('🧪 Hash Plan Inspector Test Suite');
  console.log('==================================\n');

  runHashInspectorTests()
    .then(() => performanceTest())
    .then(() => {
      console.log('\n✅ All tests completed successfully!');
      console.log('\n💡 The Hash Plan Inspector helps you understand:');
      console.log('   • Which files affect task hashing');
      console.log('   • How changes impact cache invalidation');
      console.log('   • Debug performance issues with large file sets');
      console.log('   • Optimize your workspace for better caching');
    })
    .catch(error => {
      console.error('\n❌ Test suite failed:', error);
      process.exit(1);
    });
}

module.exports = {
  runHashInspectorTests,
  analyzeHashPlan,
  performanceTest
};

