      "configurations": {},
      "parallelism": true
    },
    "check-format:quick": {
      "executor": "nx:run-script",
      "metadata": {
        "scriptContent": "pretty-quick --check",
        "runCommand": "pnpm run check-format:quick"
      },
      "parallelism": false,
      "options": {
        "script": "check-format:quick"
      },
      "configurations": {}
