      "configurations": {},
      "parallelism": true,
      "continuous": true
    },
    "populate-local-registry-storage": {
      "cache": true,
      "inputs": [
        {
          "input": "production",
          "projects": [
            "tag:npm:public"
          ]
        },
        "{workspaceRoot}/scripts/local-registry"
      ],
      "dependsOn": [
        "local-registry",
        {
          "target": "build",
          "projects": [
            "tag:npm:public"
