        "!{workspaceRoot}/packages/**/tsconfig.spec.json",
        "!{workspaceRoot}/packages/**/jest.config.{ts,js}",
        "!{workspaceRoot}/packages/**/.eslintrc.json"
      ],
      "dependsOn": [
        {
          "target": "build",
          "projects": [
            "devkit",
            "typedoc-theme"
          ]
        }
      ],
      "outputs": [
        "{workspaceRoot}/docs/external-generated",
        "{workspaceRoot}/docs/generated"
      ],
      "configurations": {},
      "parallelism": true
    },
    "lint": {
      "dependsOn": [
        "@nx/nx-source:lint-pnpm-lock"
      ],
      "cache": true,
      "inputs": [
        "{projectRoot}/pnpm-lock.yaml"
      ],
      "configurations": {},
      "options": {},
      "parallelism": true,
      "executor": "nx:noop"
