diff --git a/packages/nx/src/command-line/graph/graph.ts b/packages/nx/src/command-line/graph/graph.ts
index 5e172ab617..c33a4f239b 100644
--- a/packages/nx/src/command-line/graph/graph.ts
+++ b/packages/nx/src/command-line/graph/graph.ts
@@ -490,17 +490,29 @@ export async function generateGraph(
       !!args.file && args.file.endsWith('html') ? 'build' : 'serve'
     );

-    const { app, url } = await startServer(
-      html,
-      environmentJs,
-      args.host || '127.0.0.1',
-      args.port || 4211,
-      args.watch,
-      affectedProjects,
-      args.focus,
-      args.groupByFolder,
-      args.exclude
-    );
+    let app: http.Server;
+    let url: URL;
+    try {
+      const result = await startServer(
+        html,
+        environmentJs,
+        args.host || '127.0.0.1',
+        args.port || 4211,
+        args.watch,
+        affectedProjects,
+        args.focus,
+        args.groupByFolder,
+        args.exclude
+      );
+      app = result.app;
+      url = result.url;
+    } catch (err) {
+      output.error({
+        title: 'Failed to start graph server',
+        bodyLines: [err.message],
+      });
+      process.exit(1);
+    }

     url.pathname = args.view;

@@ -676,7 +688,15 @@ async function startServer(
   process.on('SIGINT', () => handleTermination(128 + 2));
   process.on('SIGTERM', () => handleTermination(128 + 15));

-  return new Promise<{ app: Server; url: URL }>((res) => {
+  return new Promise<{ app: Server; url: URL }>((res, rej) => {
+    app.on('error', (err: NodeJS.ErrnoException) => {
+      if (err.code === 'EADDRINUSE') {
+        rej(new Error(`Port ${port} is already in use. Please use a different port with --port option or stop the process using port ${port}.`));
+      } else {
+        rej(err);
+      }
+    });
+
     app.listen(port, host, () => {
       res({ app, url: new URL(`http://${host}:${port}`) });
     });

