152  workspace: WorkspaceTypeAndRoot,
153  isLocalInstall: boolean
154) {
155  const LOCAL_NX_VERSION: string | null = localNx
156    ? getLocalNxVersion(workspace)
157    : null;
158  const GLOBAL_NX_VERSION: string | null = isLocalInstall
159    ? null
160    : require('../package.json').version;
161
162  globalThis.GLOBAL_NX_VERSION ??= GLOBAL_NX_VERSION;
163  return { LOCAL_NX_VERSION, GLOBAL_NX_VERSION };
