202  if (process.env.NX_CLI_SET) {
203    return;
204  }
205
206  const isOutdatedGlobalInstall = checkOutdatedGlobalInstallation(
207    globalNxVersion,
208    localNxVersion
209  );
210
211  // Using a global Nx Install
212  if (isOutdatedGlobalInstall) {
213    const bodyLines = localNxVersion
214      ? [
215          `Your repository uses a higher version of Nx (${localNxVersion}) than your global CLI version (${globalNxVersion})`,
216        ]
217      : [];
218
219    bodyLines.push(
220      'For more information, see https://nx.dev/more-concepts/global-nx'
221    );
222    output.warn({
223      title: `It's time to update Nx `,
224      bodyLines,
225    });
226  }
227}
228
229function checkOutdatedGlobalInstallation(
230  globalNxVersion?: string,
231  localNxVersion?: string
232) {
233  // We aren't running a global install, so we can't know if its outdated.
234  if (!globalNxVersion) {
235    return false;
236  }
237  if (localNxVersion) {
238    // If the global Nx install is at least a major version behind the local install, warn.
239    return major(globalNxVersion) < major(localNxVersion);
240  }
241  // No local installation was detected. This can happen if the user is running a global install
242  // that contains an older version of Nx, which is unable to detect the local installation. The most
243  // recent case where this would have happened would be when we stopped generating workspace.json by default,
244  // as older global installations used it to determine the workspace root. This only be hit in rare cases,
245  // but can provide valuable insights for troubleshooting.
246  const latestVersionOfNx = getLatestVersionOfNx();
247  if (latestVersionOfNx && major(globalNxVersion) < major(latestVersionOfNx)) {
248    return true;
249  }
250}
251
252function getLocalNxVersion(workspace: WorkspaceTypeAndRoot): string | null {
253  try {
254    const { packageJson } = readModulePackageJson(
255      'nx',
256      getNxRequirePaths(workspace.dir)
257    );
258    return packageJson.version;
259  } catch {}
260}
