262function _getLatestVersionOfNx(): string {
263  try {
264    return execSync('npm view nx@latest version', {
265      windowsHide: false,
266    })
267      .toString()
268      .trim();
269  } catch {
270    try {
271      return execSync('pnpm view nx@latest version', {
272        windowsHide: false,
273      })
274        .toString()
275        .trim();
276    } catch {
277      return null;
278    }
279  }
280}
