282const getLatestVersionOfNx = ((fn: () => string) => {
283  let cache: string = null;
284  return () => cache || (cache = fn());
285})(_getLatestVersionOfNx);
286
287process.on('exit', () => {
288  removeDbConnections();
289});
290
291main().catch((error) => {
292  console.error(error);
293  process.exit(1);
