  24} from '../project-graph/plugins/tasks-execution-hooks';
  25import { createProjectGraphAsync } from '../project-graph/project-graph';
  26import { NxArgs } from '../utils/command-line-utils';
  27import { handleErrors } from '../utils/handle-errors';
  28import { isCI } from '../utils/is-ci';
  29import { isNxCloudUsed } from '../utils/nx-cloud-utils';
  30import { printNxKey } from '../utils/nx-key';
  31import { output } from '../utils/output';
  32import {
  33  collectEnabledTaskSyncGeneratorsFromTaskGraph,
  34  flushSyncGeneratorChanges,
  35  getFailedSyncGeneratorsFixMessageLines,
  36  getFlushFailureMessageLines,
  37  getSyncGeneratorChanges,
  38  getSyncGeneratorSuccessResultsMessageLines,
  39  processSyncGeneratorResultErrors,
  40} from '../utils/sync-generators';
  41import { workspaceRoot } from '../utils/workspace-root';
  42import { createTaskGraph } from './create-task-graph';
  43import { isTuiEnabled } from './is-tui-enabled';
  44import {
  45  CompositeLifeCycle,
  46  LifeCycle,
  47  TaskResult,
  48  TaskResults,
  49} from './life-cycle';
  50import { createRunManyDynamicOutputRenderer } from './life-cycles/dynamic-run-many-terminal-output-life-cycle';
  51import { createRunOneDynamicOutputRenderer } from './life-cycles/dynamic-run-one-terminal-output-life-cycle';
  52import { StaticRunManyTerminalOutputLifeCycle } from './life-cycles/static-run-many-terminal-output-life-cycle';
  53import { StaticRunOneTerminalOutputLifeCycle } from './life-cycles/static-run-one-terminal-output-life-cycle';
  54import { StoreRunInformationLifeCycle } from './life-cycles/store-run-information-life-cycle';
  55import { getTasksHistoryLifeCycle } from './life-cycles/task-history-life-cycle';
  56import { TaskProfilingLifeCycle } from './life-cycles/task-profiling-life-cycle';
  57import { TaskResultsLifeCycle } from './life-cycles/task-results-life-cycle';
  58import { TaskTimingsLifeCycle } from './life-cycles/task-timings-life-cycle';
  59import { getTuiTerminalSummaryLifeCycle } from './life-cycles/tui-summary-life-cycle';
  60import {
  61  assertTaskGraphDoesNotContainInvalidTargets,
  62  findCycle,
  63  getLeafTasks,
  64  makeAcyclic,
  65  validateNoAtomizedTasks,
  66} from './task-graph-utils';
  67import { TasksRunner, TaskStatus } from './tasks-runner';
  68import { shouldStreamOutput } from './utils';
  69import { signalToCode } from '../utils/exit-codes';
  70import chalk = require('chalk');
  71
  72const originalStdoutWrite = process.stdout.write.bind(process.stdout);
  73const originalStderrWrite = process.stderr.write.bind(process.stderr);
  74const originalConsoleLog = console.log.bind(console);
  75const originalConsoleError = console.error.bind(console);
  76
  77async function getTerminalOutputLifeCycle(
  78  initiatingProject: string,
  79  initiatingTasks: Task[],
  80  projectNames: string[],
  81  tasks: Task[],
  82  taskGraph: TaskGraph,
  83  nxArgs: NxArgs,
  84  nxJson: NxJsonConfiguration,
  85  overrides: Record<string, unknown>
  86): Promise<{
  87  lifeCycle: LifeCycle;
  88  printSummary?: () => void;
  89  restoreTerminal?: () => void;
  90  renderIsDone: Promise<void>;
  91}> {
  92  const overridesWithoutHidden = { ...overrides };
  93  delete overridesWithoutHidden['__overrides_unparsed__'];
  94
  95  const isRunOne = initiatingProject != null;
  96
  97  if (isTuiEnabled()) {
  98    const interceptedNxCloudLogs: (string | Uint8Array<ArrayBufferLike>)[] = [];
  99
 100    const createPatchedConsoleMethod = (
 101      originalMethod: typeof console.log | typeof console.error
 102    ): typeof console.log | typeof console.error => {
 103      return (...args: any[]) => {
 104        // Check if the log came from the Nx Cloud client, otherwise invoke the original write method
 105        const stackTrace = new Error().stack;
 106        const isNxCloudLog = stackTrace.includes(
 107          join(workspaceRoot, '.nx', 'cache', 'cloud')
 108        );
 109        if (!isNxCloudLog) {
 110          return originalMethod(...args);
 111        }
 112        // No-op the Nx Cloud client logs
 113      };
 114    };
 115    // The cloud client calls console.log when NX_VERBOSE_LOGGING is set to true
 116    console.log = createPatchedConsoleMethod(originalConsoleLog);
 117    console.error = createPatchedConsoleMethod(originalConsoleError);
 118
 119    const patchedWrite = (_chunk, _encoding, callback) => {
 120      // Preserve original behavior around callback and return value, just in case
 121      if (callback) {
 122        callback();
 123      }
 124      return true;
 125    };
 126
 127    process.stdout.write = patchedWrite as any;
 128    process.stderr.write = patchedWrite as any;
 129
 130    const { AppLifeCycle, restoreTerminal } = await import('../native');
 131    let appLifeCycle;
 132
 133    const isRunOne = initiatingProject != null;
 134
 135    const pinnedTasks: string[] = [];
 136    const taskText = tasks.length === 1 ? 'task' : 'tasks';
 137    const projectText = projectNames.length === 1 ? 'project' : 'projects';
 138    let titleText = '';
 139
 140    if (isRunOne) {
 141      const mainTaskId = initiatingTasks[0].id;
 142      pinnedTasks.push(mainTaskId);
 143      const mainContinuousDependencies =
 144        taskGraph.continuousDependencies[mainTaskId];
 145      if (mainContinuousDependencies.length > 0) {
 146        pinnedTasks.push(mainContinuousDependencies[0]);
 147      }
 148      const [project, target] = mainTaskId.split(':');
 149      titleText = `${target} ${project}`;
 150      if (tasks.length > 1) {
 151        titleText += `, and ${tasks.length - 1} requisite ${taskText}`;
 152      }
 153    } else {
 154      titleText =
 155        nxArgs.targets.join(', ') +
 156        ` for ${projectNames.length} ${projectText}`;
 157      if (tasks.length > projectNames.length) {
 158        titleText += `, and ${
 159          tasks.length - projectNames.length
 160        } requisite ${taskText}`;
 161      }
 162    }
 163
 164    let resolveRenderIsDonePromise: (value: void) => void;
 165    // Default renderIsDone that will be overridden if the TUI is used
 166    let renderIsDone = new Promise<void>(
 167      (resolve) => (resolveRenderIsDonePromise = resolve)
 168    );
 169
 170    const { lifeCycle: tsLifeCycle, printSummary } =
 171      getTuiTerminalSummaryLifeCycle({
 172        projectNames,
 173        tasks,
 174        taskGraph,
 175        args: nxArgs,
 176        overrides: overridesWithoutHidden,
 177        initiatingProject,
 178        initiatingTasks,
 179        resolveRenderIsDonePromise,
 180      });
 181
 182    if (tasks.length === 0) {
 183      renderIsDone = renderIsDone.then(() => {
 184        // Revert the patched methods
 185        process.stdout.write = originalStdoutWrite;
 186        process.stderr.write = originalStderrWrite;
 187        console.log = originalConsoleLog;
 188        console.error = originalConsoleError;
 189      });
 190    }
 191
 192    const lifeCycles: LifeCycle[] = [tsLifeCycle];
 193    // Only run the TUI if there are tasks to run
 194    if (tasks.length > 0) {
 195      appLifeCycle = new AppLifeCycle(
 196        tasks,
 197        initiatingTasks.map((t) => t.id),
 198        isRunOne ? RunMode.RunOne : RunMode.RunMany,
 199        pinnedTasks,
 200        nxArgs ?? {},
 201        nxJson.tui ?? {},
 202        titleText,
 203        workspaceRoot
 204      );
 205      lifeCycles.unshift(appLifeCycle);
 206
 207      /**
 208       * Patch stdout.write and stderr.write methods to pass Nx Cloud client logs to the TUI via the lifecycle
 209       */
 210      const createPatchedLogWrite = (
 211        originalWrite:
 212          | typeof process.stdout.write
 213          | typeof process.stderr.write,
 214        isError: boolean
 215      ): typeof process.stdout.write | typeof process.stderr.write => {
 216        // @ts-ignore
 217        return (chunk, encoding, callback) => {
 218          if (isError) {
 219            logDebug(
 220              Buffer.isBuffer(chunk)
 221                ? chunk.toString(encoding)
 222                : chunk.toString()
 223            );
 224          } else {
 225            logDebug(
 226              Buffer.isBuffer(chunk)
 227                ? chunk.toString(encoding)
 228                : chunk.toString()
 229            );
 230          }
 231
 232          // Check if the log came from the Nx Cloud client, otherwise invoke the original write method
 233          const stackTrace = new Error().stack;
 234          const isNxCloudLog = stackTrace.includes(
 235            join(workspaceRoot, '.nx', 'cache', 'cloud')
 236          );
 237          if (isNxCloudLog) {
 238            interceptedNxCloudLogs.push(chunk);
 239            // Do not bother to store logs with only whitespace characters, they aren't relevant for the TUI
 240            const trimmedChunk = chunk.toString().trim();
 241            if (trimmedChunk.length) {
 242              // Remove ANSI escape codes, the TUI will control the formatting
 243              appLifeCycle?.__setCloudMessage(
 244                stripVTControlCharacters(trimmedChunk)
 245              );
 246            }
 247          }
 248          // Preserve original behavior around callback and return value, just in case
 249          if (callback) {
 250            callback();
 251          }
 252          return true;
 253        };
 254      };
 255
 256      const createPatchedConsoleMethod = (
 257        originalMethod: typeof console.log | typeof console.error
 258      ): typeof console.log | typeof console.error => {
 259        return (...args: any[]) => {
 260          // Check if the log came from the Nx Cloud client, otherwise invoke the original write method
 261          const stackTrace = new Error().stack;
 262          const isNxCloudLog = stackTrace.includes(
 263            join(workspaceRoot, '.nx', 'cache', 'cloud')
 264          );
 265          if (!isNxCloudLog) {
 266            return originalMethod(...args);
 267          }
 268          // No-op the Nx Cloud client logs
 269        };
 270      };
 271
 272      process.stdout.write = createPatchedLogWrite(originalStdoutWrite, false);
 273      process.stderr.write = createPatchedLogWrite(originalStderrWrite, true);
 274
 275      // The cloud client calls console.log when NX_VERBOSE_LOGGING is set to true
 276      console.log = createPatchedConsoleMethod(originalConsoleLog);
 277      console.error = createPatchedConsoleMethod(originalConsoleError);
 278
 279      globalThis.tuiOnProcessExit = () => {
 280        restoreTerminal();
 281        // Revert the patched methods
 282        process.stdout.write = originalStdoutWrite;
 283        process.stderr.write = originalStderrWrite;
 284        console.log = originalConsoleLog;
 285        console.error = originalConsoleError;
 286        process.stdout.write('\n');
 287        // Print the intercepted Nx Cloud logs
 288        for (const log of interceptedNxCloudLogs) {
 289          const logString = log.toString().trimStart();
 290          process.stdout.write(logString);
 291          if (logString) {
 292            process.stdout.write('\n');
 293          }
 294        }
 295      };
 296
 297      renderIsDone = new Promise<void>((resolve) => {
 298        appLifeCycle.__init(() => {
 299          resolve();
 300        });
 301      }).finally(() => {
 302        restoreTerminal();
 303        // Revert the patched methods
 304        process.stdout.write = originalStdoutWrite;
 305        process.stderr.write = originalStderrWrite;
 306        console.log = originalConsoleLog;
 307        console.error = originalConsoleError;
 308      });
 309    }
 310
 311    return {
 312      lifeCycle: new CompositeLifeCycle(lifeCycles),
 313      restoreTerminal: () => {
 314        process.stdout.write = originalStdoutWrite;
 315        process.stderr.write = originalStderrWrite;
 316        console.log = originalConsoleLog;
 317        console.error = originalConsoleError;
 318        restoreTerminal();
 319      },
 320      printSummary,
 321      renderIsDone,
 322    };
 323  }
 324
 325  const { runnerOptions } = getRunner(nxArgs, nxJson);
 326  const useDynamicOutput = shouldUseDynamicLifeCycle(
 327    tasks,
 328    runnerOptions,
 329    nxArgs.outputStyle
 330  );
 331
 332  if (isRunOne) {
 333    if (useDynamicOutput) {
 334      return await createRunOneDynamicOutputRenderer({
 335        initiatingProject,
 336        tasks,
 337        args: nxArgs,
 338        overrides: overridesWithoutHidden,
 339      });
 340    }
 341    return {
 342      lifeCycle: new StaticRunOneTerminalOutputLifeCycle(
 343        initiatingProject,
 344        projectNames,
 345        tasks,
 346        nxArgs
 347      ),
 348      renderIsDone: Promise.resolve(),
 349    };
 350  } else {
 351    if (useDynamicOutput) {
 352      return await createRunManyDynamicOutputRenderer({
 353        projectNames,
 354        tasks,
 355        args: nxArgs,
 356        overrides: overridesWithoutHidden,
 357      });
 358    } else {
 359      return {
 360        lifeCycle: new StaticRunManyTerminalOutputLifeCycle(
 361          projectNames,
 362          tasks,
 363          nxArgs,
 364          overridesWithoutHidden
 365        ),
 366        renderIsDone: Promise.resolve(),
 367      };
 368    }
 369  }
 370}
