 372function createTaskGraphAndRunValidations(
 373  projectGraph: ProjectGraph,
 374  extraTargetDependencies: TargetDependencies,
 375  projectNames: string[],
 376  nxArgs: NxArgs,
 377  overrides: any,
 378  extraOptions: {
 379    excludeTaskDependencies: boolean;
 380    loadDotEnvFiles: boolean;
 381  }
 382) {
 383  const taskGraph = createTaskGraph(
 384    projectGraph,
 385    extraTargetDependencies,
 386    projectNames,
 387    nxArgs.targets,
 388    nxArgs.configuration,
 389    overrides,
 390    extraOptions.excludeTaskDependencies
 391  );
 392
 393  assertTaskGraphDoesNotContainInvalidTargets(taskGraph);
 394
 395  const cycle = findCycle(taskGraph);
 396  if (cycle) {
 397    if (process.env.NX_IGNORE_CYCLES === 'true' || nxArgs.nxIgnoreCycles) {
 398      output.warn({
 399        title: `The task graph has a circular dependency`,
 400        bodyLines: [`${cycle.join(' --> ')}`],
 401      });
 402      makeAcyclic(taskGraph);
 403    } else {
 404      output.error({
 405        title: `Could not execute command because the task graph has a circular dependency`,
 406        bodyLines: [`${cycle.join(' --> ')}`],
 407      });
 408      process.exit(1);
 409    }
 410  }
 411
 412  // validate that no atomized tasks like e2e-ci are used without Nx Cloud
 413  if (
 414    !isNxCloudUsed(readNxJson()) &&
 415    !process.env['NX_SKIP_ATOMIZER_VALIDATION']
 416  ) {
 417    validateNoAtomizedTasks(taskGraph, projectGraph);
 418  }
 419
 420  return taskGraph;
 421}
