 423export async function runCommand(
 424  projectsToRun: ProjectGraphProjectNode[],
 425  currentProjectGraph: ProjectGraph,
 426  { nxJson }: { nxJson: NxJsonConfiguration },
 427  nxArgs: NxArgs,
 428  overrides: any,
 429  initiatingProject: string | null,
 430  extraTargetDependencies: Record<string, (TargetDependencyConfig | string)[]>,
 431  extraOptions: { excludeTaskDependencies: boolean; loadDotEnvFiles: boolean }
 432): Promise<NodeJS.Process['exitCode']> {
 433  const status = await handleErrors(
 434    process.env.NX_VERBOSE_LOGGING === 'true',
 435    async () => {
 436      await runPreTasksExecution({
 437        workspaceRoot,
 438        nxJsonConfiguration: nxJson,
 439      });
 440
 441      const { taskResults, completed } = await runCommandForTasks(
 442        projectsToRun,
 443        currentProjectGraph,
 444        { nxJson },
 445        {
 446          ...nxArgs,
 447          skipNxCache:
 448            nxArgs.skipNxCache ||
 449            process.env.NX_SKIP_NX_CACHE === 'true' ||
 450            process.env.NX_DISABLE_NX_CACHE === 'true',
 451        },
 452        overrides,
 453        initiatingProject,
 454        extraTargetDependencies,
 455        extraOptions
 456      );
 457
 458      const exitCode = !completed
 459        ? signalToCode('SIGINT')
 460        : Object.values(taskResults).some(
 461            (taskResult) =>
 462              taskResult.status === 'failure' || taskResult.status === 'skipped'
 463          )
 464        ? 1
 465        : 0;
 466
 467      await runPostTasksExecution({
 468        taskResults,
 469        workspaceRoot,
 470        nxJsonConfiguration: nxJson,
 471      });
 472
 473      return exitCode;
 474    }
 475  );
 476
 477  return status;
 478}
