 480export async function runCommandForTasks(
 481  projectsToRun: ProjectGraphProjectNode[],
 482  currentProjectGraph: ProjectGraph,
 483  { nxJson }: { nxJson: NxJsonConfiguration },
 484  nxArgs: NxArgs,
 485  overrides: any,
 486  initiatingProject: string | null,
 487  extraTargetDependencies: Record<string, (TargetDependencyConfig | string)[]>,
 488  extraOptions: { excludeTaskDependencies: boolean; loadDotEnvFiles: boolean }
 489): Promise<{ taskResults: TaskResults; completed: boolean }> {
 490  const projectNames = projectsToRun.map((t) => t.name);
 491  const projectNameSet = new Set(projectNames);
 492
 493  const { projectGraph, taskGraph } = await ensureWorkspaceIsInSyncAndGetGraphs(
 494    currentProjectGraph,
 495    nxJson,
 496    projectNames,
 497    nxArgs,
 498    overrides,
 499    extraTargetDependencies,
 500    extraOptions
 501  );
 502  const tasks = Object.values(taskGraph.tasks);
 503
 504  const initiatingTasks = tasks.filter(
 505    (t) =>
 506      projectNameSet.has(t.target.project) &&
 507      nxArgs.targets.includes(t.target.target)
 508  );
 509
 510  const { lifeCycle, renderIsDone, printSummary, restoreTerminal } =
 511    await getTerminalOutputLifeCycle(
 512      initiatingProject,
 513      initiatingTasks,
 514      projectNames,
 515      tasks,
 516      taskGraph,
 517      nxArgs,
 518      nxJson,
 519      overrides
 520    );
 521
 522  try {
 523    const taskResults = await invokeTasksRunner({
 524      tasks,
 525      projectGraph,
 526      taskGraph,
 527      lifeCycle,
 528      nxJson,
 529      nxArgs,
 530      loadDotEnvFiles: extraOptions.loadDotEnvFiles,
 531      initiatingProject,
 532      initiatingTasks,
 533    });
 534
 535    await renderIsDone;
 536
 537    if (printSummary) {
 538      printSummary();
 539    }
 540
 541    await printNxKey();
 542
 543    return {
 544      taskResults,
 545      completed: didCommandComplete(tasks, taskGraph, taskResults),
 546    };
 547  } catch (e) {
 548    if (restoreTerminal) {
 549      restoreTerminal();
 550    }
 551    throw e;
 552  }
 553}
 554
 555function didCommandComplete(
 556  tasks: Task[],
 557  taskGraph: TaskGraph,
 558  taskResults: TaskResults
 559): boolean {
 560  // If no tasks, then we can consider it complete
 561  if (tasks.length === 0) {
 562    return true;
 563  }
 564
 565  let continousLeafTasks = false;
 566  const leafTasks = getLeafTasks(taskGraph);
 567  for (const task of tasks) {
 568    if (!task.continuous) {
 569      // If any discrete task does not have a result then it did not run
 570      if (!taskResults[task.id]) {
 571        return false;
 572      }
 573    } else {
 574      if (leafTasks.has(task.id)) {
 575        continousLeafTasks = true;
 576      }
 577    }
 578  }
 579
 580  // If a leaf task is continous, we must have cancelled it.
 581  // Otherwise, we've looped through all the discrete tasks and they have results
 582  return !continousLeafTasks;
 583}
 584
 585async function ensureWorkspaceIsInSyncAndGetGraphs(
 586  projectGraph: ProjectGraph,
 587  nxJson: NxJsonConfiguration,
 588  projectNames: string[],
 589  nxArgs: NxArgs,
 590  overrides: any,
 591  extraTargetDependencies: Record<string, (TargetDependencyConfig | string)[]>,
 592  extraOptions: { excludeTaskDependencies: boolean; loadDotEnvFiles: boolean }
 593): Promise<{
 594  projectGraph: ProjectGraph;
 595  taskGraph: TaskGraph;
 596}> {
 597  let taskGraph = createTaskGraphAndRunValidations(
 598    projectGraph,
 599    extraTargetDependencies ?? {},
 600    projectNames,
 601    nxArgs,
 602    overrides,
 603    extraOptions
 604  );
 605
 606  if (nxArgs.skipSync || isCI()) {
 607    return { projectGraph, taskGraph };
 608  }
 609
 610  // collect unique syncGenerators from the tasks
 611  const uniqueSyncGenerators = collectEnabledTaskSyncGeneratorsFromTaskGraph(
 612    taskGraph,
 613    projectGraph,
 614    nxJson
 615  );
 616
 617  if (!uniqueSyncGenerators.size) {
 618    // There are no sync generators registered in the tasks to run
 619    return { projectGraph, taskGraph };
 620  }
 621
 622  const syncGenerators = Array.from(uniqueSyncGenerators);
 623  const results = await getSyncGeneratorChanges(syncGenerators);
 624  if (!results.length) {
 625    // There are no changes to sync, workspace is up to date
 626    return { projectGraph, taskGraph };
 627  }
 628
 629  const {
 630    failedGeneratorsCount,
 631    areAllResultsFailures,
 632    anySyncGeneratorsFailed,
 633  } = processSyncGeneratorResultErrors(results);
 634  const failedSyncGeneratorsFixMessageLines =
 635    getFailedSyncGeneratorsFixMessageLines(results, nxArgs.verbose);
 636  const outOfSyncTitle = 'The workspace is out of sync';
 637  const resultBodyLines = getSyncGeneratorSuccessResultsMessageLines(results);
 638  const fixMessage =
 639    'Make sure to run `nx sync` to apply the identified changes or set `sync.applyChanges` to `true` in your `nx.json` to apply them automatically when running tasks in interactive environments.';
 640
 641  if (!process.stdout.isTTY) {
 642    // If the user is running a non-TTY environment we
 643    // throw an error to stop the execution of the tasks.
 644    if (areAllResultsFailures) {
 645      output.error({
 646        title: `The workspace is probably out of sync because ${
 647          failedGeneratorsCount === 1
 648            ? 'a sync generator'
 649            : 'some sync generators'
 650        } failed to run`,
 651        bodyLines: failedSyncGeneratorsFixMessageLines,
 652      });
 653    } else {
 654      output.error({
 655        title: outOfSyncTitle,
 656        bodyLines: [...resultBodyLines, '', fixMessage],
 657      });
 658
 659      if (anySyncGeneratorsFailed) {
 660        output.error({
 661          title:
 662            failedGeneratorsCount === 1
 663              ? 'A sync generator failed to run'
 664              : 'Some sync generators failed to run',
 665          bodyLines: failedSyncGeneratorsFixMessageLines,
 666        });
 667      }
 668    }
 669
 670    process.exit(1);
 671  }
 672
 673  if (areAllResultsFailures) {
 674    output.warn({
 675      title: `The workspace is probably out of sync because ${
 676        failedGeneratorsCount === 1
 677          ? 'a sync generator'
 678          : 'some sync generators'
 679      } failed to run`,
 680      bodyLines: failedSyncGeneratorsFixMessageLines,
 681    });
 682
 683    await confirmRunningTasksWithSyncFailures();
 684
 685    // if all sync generators failed to run there's nothing to sync, we just let the tasks run
 686    return { projectGraph, taskGraph };
 687  }
 688
 689  if (nxJson.sync?.applyChanges === false) {
 690    // If the user has set `sync.applyChanges` to `false` in their `nx.json`
 691    // we don't prompt the them and just log a warning informing them that
 692    // the workspace is out of sync and they have it set to not apply changes
 693    // automatically.
 694    output.warn({
 695      title: outOfSyncTitle,
 696      bodyLines: [
 697        ...resultBodyLines,
 698        '',
 699        'Your workspace is set to not apply the identified changes automatically (`sync.applyChanges` is set to `false` in your `nx.json`).',
 700        fixMessage,
 701      ],
 702    });
 703
 704    if (anySyncGeneratorsFailed) {
 705      output.warn({
 706        title:
 707          failedGeneratorsCount === 1
 708            ? 'A sync generator failed to run'
 709            : 'Some sync generators failed to run',
 710        bodyLines: failedSyncGeneratorsFixMessageLines,
 711      });
 712
 713      await confirmRunningTasksWithSyncFailures();
 714    }
 715
 716    return { projectGraph, taskGraph };
 717  }
 718
 719  output.warn({
 720    title: outOfSyncTitle,
 721    bodyLines: [
 722      ...resultBodyLines,
 723      ...(nxJson.sync?.applyChanges === true
 724        ? [
 725            '',
 726            'Proceeding to sync the identified changes automatically (`sync.applyChanges` is set to `true` in your `nx.json`).',
 727          ]
 728        : []),
 729    ],
 730  });
 731
 732  const applyChanges =
 733    nxJson.sync?.applyChanges === true ||
 734    (await promptForApplyingSyncGeneratorChanges());
 735
 736  if (applyChanges) {
 737    const spinner = ora('Syncing the workspace...');
 738    spinner.start();
 739
 740    // Flush sync generator changes to disk
 741    const flushResult = await flushSyncGeneratorChanges(results);
 742
 743    if ('generatorFailures' in flushResult) {
 744      spinner.fail();
 745      output.error({
 746        title: 'Failed to sync the workspace',
 747        bodyLines: [
 748          ...getFlushFailureMessageLines(flushResult, nxArgs.verbose),
 749          ...(flushResult.generalFailure
 750            ? [
 751                'If needed, you can run the tasks with the `--skip-sync` flag to disable syncing.',
 752              ]
 753            : []),
 754        ],
 755      });
 756
 757      await confirmRunningTasksWithSyncFailures();
 758    }
 759
 760    // Re-create project graph and task graph
 761    projectGraph = await createProjectGraphAsync();
 762    taskGraph = createTaskGraphAndRunValidations(
 763      projectGraph,
 764      extraTargetDependencies ?? {},
 765      projectNames,
 766      nxArgs,
 767      overrides,
 768      extraOptions
 769    );
 770
 771    const successTitle = anySyncGeneratorsFailed
 772      ? // the identified changes were synced successfully, but the workspace
 773        // is still not up to date, which we'll mention next
 774        'The identified changes were synced successfully!'
 775      : // the workspace is fully up to date
 776        'The workspace was synced successfully!';
 777    const successSubtitle =
 778      nxJson.sync?.applyChanges === true
 779        ? 'Please make sure to commit the changes to your repository or this will error in CI.'
 780        : // The user was prompted and we already logged a message about erroring in CI
 781          // so here we just tell them to commit the changes.
 782          'Please make sure to commit the changes to your repository.';
 783    spinner.succeed(`${successTitle}\n\n${successSubtitle}`);
 784
 785    if (anySyncGeneratorsFailed) {
 786      output.warn({
 787        title: `The workspace is probably still out of sync because ${
 788          failedGeneratorsCount === 1
 789            ? 'a sync generator'
 790            : 'some sync generators'
 791        } failed to run`,
 792        bodyLines: failedSyncGeneratorsFixMessageLines,
 793      });
 794
 795      await confirmRunningTasksWithSyncFailures();
 796    }
 797  } else {
 798    if (anySyncGeneratorsFailed) {
 799      output.warn({
 800        title:
 801          failedGeneratorsCount === 1
 802            ? 'A sync generator failed to report the sync status'
 803            : 'Some sync generators failed to report the sync status',
 804        bodyLines: failedSyncGeneratorsFixMessageLines,
 805      });
 806
 807      await confirmRunningTasksWithSyncFailures();
 808    } else {
 809      output.warn({
 810        title: 'Syncing the workspace was skipped',
 811        bodyLines: [
 812          'This could lead to unexpected results or errors when running tasks.',
 813          fixMessage,
 814        ],
 815      });
 816    }
 817  }
 818
 819  return { projectGraph, taskGraph };
 820}
 821
 822async function promptForApplyingSyncGeneratorChanges(): Promise<boolean> {
 823  try {
 824    const promptConfig = {
 825      name: 'applyChanges',
 826      type: 'autocomplete',
 827      message:
 828        'Would you like to sync the identified changes to get your workspace up to date?',
 829      choices: [
 830        {
 831          name: 'yes',
 832          message: 'Yes, sync the changes and run the tasks',
 833        },
 834        {
 835          name: 'no',
 836          message: 'No, run the tasks without syncing the changes',
 837        },
 838      ],
 839      footer: () =>
 840        chalk.dim(
 841          '\nYou can skip this prompt by setting the `sync.applyChanges` option to `true` in your `nx.json`.\nFor more information, refer to the docs: https://nx.dev/concepts/sync-generators.'
 842        ),
 843    };
 844
 845    return await prompt<{ applyChanges: 'yes' | 'no' }>([promptConfig]).then(
 846      ({ applyChanges }) => applyChanges === 'yes'
 847    );
 848  } catch {
 849    process.exit(1);
 850  }
 851}
