 853async function confirmRunningTasksWithSyncFailures(): Promise<void> {
 854  try {
 855    const promptConfig = {
 856      name: 'runTasks',
 857      type: 'autocomplete',
 858      message:
 859        'Would you like to ignore the sync failures and continue running the tasks?',
 860      choices: [
 861        {
 862          name: 'yes',
 863          message: 'Yes, ignore the failures and run the tasks',
 864        },
 865        {
 866          name: 'no',
 867          message: `No, don't run the tasks`,
 868        },
 869      ],
 870      footer: () =>
 871        chalk.dim(
 872          `\nWhen running in CI and there are sync failures, the tasks won't run. Addressing the errors above is highly recommended to prevent failures in CI.`
 873        ),
 874    };
 875
 876    const runTasks = await prompt<{ runTasks: 'yes' | 'no' }>([
 877      promptConfig,
 878    ]).then(({ runTasks }) => runTasks === 'yes');
 879
 880    if (!runTasks) {
 881      process.exit(1);
 882    }
 883  } catch {
 884    process.exit(1);
 885  }
 886}
