 888export function setEnvVarsBasedOnArgs(
 889  nxArgs: NxArgs,
 890  loadDotEnvFiles: boolean
 891) {
 892  if (
 893    nxArgs.outputStyle == 'stream' ||
 894    process.env.NX_BATCH_MODE === 'true' ||
 895    nxArgs.batch
 896  ) {
 897    process.env.NX_STREAM_OUTPUT = 'true';
 898    process.env.NX_PREFIX_OUTPUT = 'true';
 899  }
 900  if (nxArgs.outputStyle == 'stream-without-prefixes') {
 901    process.env.NX_STREAM_OUTPUT = 'true';
 902  }
 903  if (loadDotEnvFiles) {
 904    process.env.NX_LOAD_DOT_ENV_FILES = 'true';
 905  }
 906}
 907
 908export async function invokeTasksRunner({
 909  tasks,
 910  projectGraph,
 911  taskGraph,
 912  lifeCycle,
 913  nxJson,
 914  nxArgs,
 915  loadDotEnvFiles,
 916  initiatingProject,
 917  initiatingTasks,
 918}: {
 919  tasks: Task[];
 920  projectGraph: ProjectGraph;
 921  taskGraph: TaskGraph;
 922  lifeCycle: LifeCycle;
 923  nxJson: NxJsonConfiguration;
 924  nxArgs: NxArgs;
 925  loadDotEnvFiles: boolean;
 926  initiatingProject: string | null;
 927  initiatingTasks: Task[];
 928}): Promise<{ [id: string]: TaskResult }> {
 929  setEnvVarsBasedOnArgs(nxArgs, loadDotEnvFiles);
 930
 931  // this needs to be done before we start to run the tasks
 932  const taskDetails = getTaskDetails();
 933
 934  const { tasksRunner, runnerOptions } = getRunner(nxArgs, nxJson);
 935
 936  let hasher = createTaskHasher(projectGraph, nxJson, runnerOptions);
 937
 938  // this is used for two reasons: to fetch all remote cache hits AND
 939  // to submit everything that is known in advance to Nx Cloud to run in
 940  // a distributed fashion
 941
 942  await hashTasksThatDoNotDependOnOutputsOfOtherTasks(
 943    hasher,
 944    projectGraph,
 945    taskGraph,
 946    nxJson,
 947    taskDetails
 948  );
 949  const taskResultsLifecycle = new TaskResultsLifeCycle();
 950  const compositedLifeCycle: LifeCycle = new CompositeLifeCycle([
 951    ...constructLifeCycles(lifeCycle),
 952    taskResultsLifecycle,
 953  ]);
 954
 955  let promiseOrObservable:
 956    | Observable<{ task: Task; success: boolean }>
 957    | Promise<{ [id: string]: TaskStatus }> = tasksRunner(
 958    tasks,
 959    {
 960      ...runnerOptions,
 961      lifeCycle: compositedLifeCycle,
 962    },
 963    {
 964      initiatingProject,
 965      initiatingTasks,
 966      projectGraph,
 967      nxJson,
 968      nxArgs,
 969      taskGraph,
 970      hasher: {
 971        hashTask(task: Task, taskGraph_?: TaskGraph, env?: NodeJS.ProcessEnv) {
 972          if (!taskGraph_) {
 973            output.warn({
 974              title: `TaskGraph is now required as an argument to hashTask`,
 975              bodyLines: [
 976                `The TaskGraph object can be retrieved from the context`,
 977                'This will result in an error in Nx 20',
 978              ],
 979            });
 980            taskGraph_ = taskGraph;
 981          }
 982          if (!env) {
 983            output.warn({
 984              title: `The environment variables are now required as an argument to hashTask`,
 985              bodyLines: [
 986                `Please pass the environment variables used when running the task`,
 987                'This will result in an error in Nx 20',
 988              ],
 989            });
 990            env = process.env;
 991          }
 992          return hasher.hashTask(task, taskGraph_, env);
 993        },
 994        hashTasks(
 995          task: Task[],
 996          taskGraph_?: TaskGraph,
 997          env?: NodeJS.ProcessEnv
 998        ) {
 999          if (!taskGraph_) {
1000            output.warn({
1001              title: `TaskGraph is now required as an argument to hashTasks`,
1002              bodyLines: [
1003                `The TaskGraph object can be retrieved from the context`,
1004                'This will result in an error in Nx 20',
1005              ],
1006            });
1007            taskGraph_ = taskGraph;
1008          }
1009          if (!env) {
1010            output.warn({
1011              title: `The environment variables are now required as an argument to hashTasks`,
1012              bodyLines: [
1013                `Please pass the environment variables used when running the tasks`,
1014                'This will result in an error in Nx 20',
1015              ],
1016            });
1017            env = process.env;
1018          }
1019
1020          return hasher.hashTasks(task, taskGraph_, env);
1021        },
1022      },
1023      daemon: daemonClient,
1024    }
1025  );
1026  if ((promiseOrObservable as any).subscribe) {
1027    promiseOrObservable = convertObservableToPromise(
1028      promiseOrObservable as Observable<{ task: Task; success: boolean }>
1029    );
1030  }
1031
1032  await (promiseOrObservable as Promise<{
1033    [id: string]: TaskStatus;
1034  }>);
1035  return taskResultsLifecycle.getTaskResults();
1036}
1037
1038export function constructLifeCycles(lifeCycle: LifeCycle): LifeCycle[] {
1039  const lifeCycles = [] as LifeCycle[];
1040  lifeCycles.push(new StoreRunInformationLifeCycle());
1041  lifeCycles.push(lifeCycle);
1042  if (process.env.NX_PERF_LOGGING === 'true') {
1043    lifeCycles.push(new TaskTimingsLifeCycle());
1044  }
1045  if (process.env.NX_PROFILE) {
1046    lifeCycles.push(new TaskProfilingLifeCycle(process.env.NX_PROFILE));
1047  }
1048  const historyLifeCycle = getTasksHistoryLifeCycle();
1049  if (historyLifeCycle) {
1050    lifeCycles.push(historyLifeCycle);
1051  }
1052  return lifeCycles;
1053}
1054
1055async function convertObservableToPromise(
1056  obs: Observable<{ task: Task; success: boolean }>
1057): Promise<{ [id: string]: TaskStatus }> {
1058  return await new Promise((res) => {
1059    let tasksResults: { [id: string]: TaskStatus } = {};
1060    obs.subscribe({
1061      next: (t) => {
1062        tasksResults[t.task.id] = t.success ? 'success' : 'failure';
1063      },
1064      error: (error) => {
1065        output.error({
1066          title: 'Unhandled error in task executor',
1067        });
1068        console.error(error);
1069        res(tasksResults);
1070      },
1071      complete: () => {
1072        res(tasksResults);
1073      },
1074    });
1075  });
1076}
1077
1078function shouldUseDynamicLifeCycle(
1079  tasks: Task[],
1080  options: any,
1081  outputStyle: string
1082) {
1083  if (
1084    process.env.NX_BATCH_MODE === 'true' ||
1085    process.env.NX_VERBOSE_LOGGING === 'true' ||
1086    process.env.NX_TASKS_RUNNER_DYNAMIC_OUTPUT === 'false'
1087  ) {
1088    return false;
1089  }
1090  if (!process.stdout.isTTY) return false;
1091  if (isCI()) return false;
1092  if (outputStyle === 'static' || outputStyle === 'stream') return false;
1093
1094  return !tasks.find((t) => shouldStreamOutput(t, null));
1095}
1096
1097function loadTasksRunner(modulePath: string): TasksRunner {
1098  try {
1099    const maybeTasksRunner = require(modulePath) as
1100      | TasksRunner
1101      | { default: TasksRunner };
1102    // to support both babel and ts formats
1103    return 'default' in maybeTasksRunner
1104      ? maybeTasksRunner.default
1105      : maybeTasksRunner;
1106  } catch (e) {
1107    if (
1108      e.code === 'MODULE_NOT_FOUND' &&
1109      (modulePath === 'nx-cloud' || modulePath === '@nrwl/nx-cloud')
1110    ) {
1111      return require('../nx-cloud/nx-cloud-tasks-runner-shell')
1112        .nxCloudTasksRunnerShell;
1113    }
1114    throw e;
1115  }
1116}
1117
1118export function getRunner(
1119  nxArgs: NxArgs,
1120  nxJson: NxJsonConfiguration
1121): {
1122  tasksRunner: TasksRunner;
1123  runnerOptions: any;
1124} {
1125  let runner = nxArgs.runner;
1126  runner = runner ?? 'default';
1127
1128  if (runner !== 'default' && !nxJson.tasksRunnerOptions?.[runner]) {
1129    throw new Error(`Could not find runner configuration for ${runner}`);
1130  }
1131
1132  const modulePath: string = getTasksRunnerPath(runner, nxJson);
1133
1134  try {
1135    const tasksRunner = loadTasksRunner(modulePath);
1136
1137    return {
1138      tasksRunner,
1139      runnerOptions: getRunnerOptions(
1140        runner,
1141        nxJson,
1142        nxArgs,
1143        modulePath === 'nx-cloud'
1144      ),
1145    };
1146  } catch {
1147    throw new Error(`Could not find runner configuration for ${runner}`);
1148  }
1149}
1150
1151const defaultTasksRunnerPath = require.resolve('./default-tasks-runner');
1152
1153function getTasksRunnerPath(
1154  runner: string,
1155  nxJson: NxJsonConfiguration<string[] | '*'>
1156) {
1157  // If running inside of Codex, there will be no internet access, so we cannot use the cloud runner, regardless of other config.
1158  // We can infer this scenario by checking for certain environment variables defined in their base image: https://github.com/openai/codex-universal
1159  if (process.env.CODEX_ENV_NODE_VERSION) {
1160    output.warn({
1161      title: 'Codex environment detected, using default tasks runner',
1162      bodyLines: [
1163        'Codex does not have internet access when it runs tasks, so Nx will use the default tasks runner and only leverage local caching.',
1164      ],
1165    });
1166    return defaultTasksRunnerPath;
1167  }
1168
1169  const isCloudRunner =
1170    // No tasksRunnerOptions for given --runner
1171    nxJson.nxCloudAccessToken ||
1172    // No runner prop in tasks runner options, check if access token is set.
1173    nxJson.tasksRunnerOptions?.[runner]?.options?.accessToken ||
1174    ['nx-cloud', '@nrwl/nx-cloud'].includes(
1175      nxJson.tasksRunnerOptions?.[runner]?.runner
1176    ) ||
1177    // Cloud access token specified in env var.
1178    process.env.NX_CLOUD_ACCESS_TOKEN ||
1179    // Nx Cloud ID specified in nxJson
1180    nxJson.nxCloudId;
1181
1182  return isCloudRunner ? 'nx-cloud' : defaultTasksRunnerPath;
1183}
1184
1185export function getRunnerOptions(
1186  runner: string,
1187  nxJson: NxJsonConfiguration<string[] | '*'>,
1188  nxArgs: NxArgs,
1189  isCloudDefault: boolean
1190): any {
1191  const defaultCacheableOperations = [];
1192
1193  for (const key in nxJson.targetDefaults) {
1194    if (nxJson.targetDefaults[key].cache) {
1195      defaultCacheableOperations.push(key);
1196    }
1197  }
1198
1199  const result = {
1200    ...nxJson.tasksRunnerOptions?.[runner]?.options,
1201    ...nxArgs,
1202  };
1203
1204  // NOTE: we don't pull from env here because the cloud package
1205  // supports it within nx-cloud's implementation. We could
1206  // normalize it here, and that may make more sense, but
1207  // leaving it as is for now.
1208  if (nxJson.nxCloudAccessToken && isCloudDefault) {
1209    result.accessToken ??= nxJson.nxCloudAccessToken;
1210  }
1211
1212  if (nxJson.nxCloudId && isCloudDefault) {
1213    result.nxCloudId ??= nxJson.nxCloudId;
1214  }
1215
1216  if (nxJson.nxCloudUrl && isCloudDefault) {
1217    result.url ??= nxJson.nxCloudUrl;
1218  }
1219
1220  if (nxJson.nxCloudEncryptionKey && isCloudDefault) {
1221    result.encryptionKey ??= nxJson.nxCloudEncryptionKey;
1222  }
1223
1224  if (nxJson.parallel) {
1225    result.parallel ??= nxJson.parallel;
1226  }
1227
1228  if (nxJson.cacheDirectory) {
1229    result.cacheDirectory ??= nxJson.cacheDirectory;
1230  }
1231
1232  if (defaultCacheableOperations.length) {
1233    result.cacheableOperations ??= [];
1234    result.cacheableOperations = result.cacheableOperations.concat(
1235      defaultCacheableOperations
1236    );
1237  }
1238
1239  if (nxJson.useDaemonProcess !== undefined) {
1240    result.useDaemonProcess ??= nxJson.useDaemonProcess;
1241  }
1242
1243  return result;
1244}
