  1	import { minimatch } from 'minimatch';
  2	import { existsSync } from 'node:fs';
  3	import { dirname, join } from 'node:path';
  4	
  5	import { NxJsonConfiguration, readNxJson } from '../../config/nx-json';
  6	import type { ProjectConfiguration } from '../../config/workspace-json-project-json';
  7	import { toProjectName } from '../../config/to-project-name';
  8	import { readJsonFile, readYamlFile } from '../../utils/fileutils';
  9	import { combineGlobPatterns } from '../../utils/globs';
 10	import { NX_PREFIX } from '../../utils/logger';
 11	import { output } from '../../utils/output';
 12	import {
 13	  getMetadataFromPackageJson,
 14	  PackageJson,
 15	  getTagsFromPackageJson,
 16	  readTargetsFromPackageJson,
 17	} from '../../utils/package-json';
 18	import { joinPathFragments } from '../../utils/path';
 19	import {
 20	  createNodesFromFiles,
 21	  CreateNodesV2,
 22	} from '../../project-graph/plugins';
 23	import { basename } from 'path';
 24	import { hashObject } from '../../hasher/file-hasher';
 25	import {
 26	  PackageJsonConfigurationCache,
 27	  readPackageJsonConfigurationCache,
 28	} from '../../../plugins/package-json';
 29	
 30	export const createNodesV2: CreateNodesV2 = [
 31	  combineGlobPatterns(
 32	    'package.json',
 33	    '**/package.json',
 34	    'project.json',
 35	    '**/project.json'
 36	  ),
 37	  (configFiles, _, context) => {
 38	    const { packageJsons, projectJsonRoots } = splitConfigFiles(configFiles);
 39	
 40	    const readJson = (f) => readJsonFile(join(context.workspaceRoot, f));
 41	    const isInPackageJsonWorkspaces = buildPackageJsonWorkspacesMatcher(
 42	      context.workspaceRoot,
 43	      readJson
 44	    );
 45	    const isNextToProjectJson = (packageJsonPath: string) => {
 46	      return projectJsonRoots.has(dirname(packageJsonPath));
 47	    };
 48	
 49	    const cache = readPackageJsonConfigurationCache();
 50	
 51	    return createNodesFromFiles(
 52	      (packageJsonPath, options, context) => {
 53	        const isInPackageManagerWorkspaces =
 54	          isInPackageJsonWorkspaces(packageJsonPath);
 55	        if (
 56	          !isInPackageManagerWorkspaces &&
 57	          !isNextToProjectJson(packageJsonPath)
 58	        ) {
 59	          // Skip if package.json is not part of the package.json workspaces and not next to a project.json.
 60	          return null;
 61	        }
 62	
 63	        return createNodeFromPackageJson(
 64	          packageJsonPath,
 65	          context.workspaceRoot,
 66	          cache,
 67	          isInPackageManagerWorkspaces
 68	        );
 69	      },
 70	      packageJsons,
 71	      _,
 72	      context
 73	    );
 74	  },
 75	];
 76	
 77	function splitConfigFiles(configFiles: readonly string[]): {
 78	  packageJsons: string[];
 79	  projectJsonRoots: Set<string>;
 80	} {
 81	  const packageJsons = [];
 82	  const projectJsonRoots = new Set<string>();
 83	
 84	  for (const configFile of configFiles) {
 85	    if (basename(configFile) === 'package.json') {
 86	      packageJsons.push(configFile);
 87	    } else {
 88	      projectJsonRoots.add(dirname(configFile));
 89	    }
 90	  }
 91	
 92	  return { packageJsons, projectJsonRoots };
 93	}
 94	
 95	export function buildPackageJsonWorkspacesMatcher(
 96	  workspaceRoot: string,
 97	  readJson: (path: string) => any
 98	) {
 99	  const patterns = getGlobPatternsFromPackageManagerWorkspaces(
100	    workspaceRoot,
101	    readJson
102	  );
103	
104	  const negativePatterns = patterns.filter((p) => p.startsWith('!'));
105	  const positivePatterns = patterns.filter((p) => !p.startsWith('!'));
106	
107	  if (
108	    // There are some negative patterns
109	    negativePatterns.length > 0 &&
110	    // No positive patterns
111	    (positivePatterns.length === 0 ||
112	      // Or only a single positive pattern that is the default coming from root package
113	      (positivePatterns.length === 1 && positivePatterns[0] === 'package.json'))
114	  ) {
115	    positivePatterns.push('**/package.json');
116	  }
117	
118	  return (p: string) =>
119	    positivePatterns.some((positive) => minimatch(p, positive)) &&
120	    /**
121	     * minimatch will return true if the given p is NOT excluded by the negative pattern.
122	     *
123	     * For example if the negative pattern is "!packages/vite", then the given p "packages/vite" will return false,
124	     * the given p "packages/something-else/package.json" will return true.
125	     *
126	     * Therefore, we need to ensure that every negative pattern returns true to validate that the given p is not
127	     * excluded by any of the negative patterns.
128	     */
129	    negativePatterns.every((negative) => minimatch(p, negative));
130	}
131	
132	export function createNodeFromPackageJson(
133	  pkgJsonPath: string,
134	  workspaceRoot: string,
135	  cache: PackageJsonConfigurationCache,
136	  isInPackageManagerWorkspaces: boolean
137	) {
138	  const json: PackageJson = readJsonFile(join(workspaceRoot, pkgJsonPath));
139	
140	  const projectRoot = dirname(pkgJsonPath);
141	
142	  const hash = hashObject({
143	    ...json,
144	    root: projectRoot,
145	    isInPackageManagerWorkspaces,
146	    // change this to bust the cache when making changes that result in different
147	    // results for the same hash
148	    bust: 1,
149	  });
150	
151	  const cached = cache[hash];
152	  if (cached) {
153	    return {
154	      projects: {
155	        [cached.root]: cached,
156	      },
157	    };
158	  }
159	
160	  const project = buildProjectConfigurationFromPackageJson(
161	    json,
162	    workspaceRoot,
163	    pkgJsonPath,
164	    readNxJson(workspaceRoot),
165	    isInPackageManagerWorkspaces
166	  );
167	
168	  cache[hash] = project;
169	  return {
170	    projects: {
171	      [project.root]: project,
172	    },
173	  };
174	}
175	
176	export function buildProjectConfigurationFromPackageJson(
177	  packageJson: PackageJson,
178	  workspaceRoot: string,
179	  packageJsonPath: string,
180	  nxJson: NxJsonConfiguration,
181	  isInPackageManagerWorkspaces: boolean
182	): ProjectConfiguration & { name: string } {
183	  const normalizedPath = packageJsonPath.split('\\').join('/');
184	  const projectRoot = dirname(normalizedPath);
185	
186	  const siblingProjectJson = tryReadJson<ProjectConfiguration>(
187	    join(workspaceRoot, projectRoot, 'project.json')
188	  );
189	
190	  if (siblingProjectJson) {
191	    for (const target of Object.keys(siblingProjectJson?.targets ?? {})) {
192	      const { executor, command, options } = siblingProjectJson.targets[target];
193	      if (
194	        // will use run-commands, different target
195	        command ||
196	        // Either uses a different executor or runs a different script
197	        (executor &&
198	          (executor !== 'nx:run-script' || options?.script !== target))
199	      ) {
200	        delete packageJson.scripts?.[target];
201	      }
202	    }
203	  }
204	
205	  if (!packageJson.name && projectRoot === '.') {
206	    throw new Error(
207	      'Nx requires the root package.json to specify a name if it is being used as an Nx project.'
208	    );
209	  }
210	
211	  let name = packageJson.name ?? toProjectName(normalizedPath);
212	
213	  const projectConfiguration: ProjectConfiguration & { name: string } = {
214	    root: projectRoot,
215	    name,
216	    ...packageJson.nx,
217	    targets: readTargetsFromPackageJson(
218	      packageJson,
219	      nxJson,
220	      projectRoot,
221	      workspaceRoot
222	    ),
223	    tags: getTagsFromPackageJson(packageJson),
224	    metadata: getMetadataFromPackageJson(
225	      packageJson,
226	      isInPackageManagerWorkspaces
227	    ),
228	  };
229	
230	  if (
231	    nxJson?.workspaceLayout?.appsDir != nxJson?.workspaceLayout?.libsDir &&
232	    nxJson?.workspaceLayout?.appsDir &&
233	    projectRoot.startsWith(nxJson.workspaceLayout.appsDir)
234	  ) {
235	    projectConfiguration.projectType = 'application';
236	  } else if (
237	    typeof nxJson?.workspaceLayout?.libsDir !== 'undefined' &&
238	    projectRoot.startsWith(nxJson.workspaceLayout.libsDir)
239	  ) {
240	    projectConfiguration.projectType = 'library';
241	  }
242	
243	  return projectConfiguration;
244	}
245	
246	/**
247	 * Get the package.json globs from package manager workspaces
248	 */
249	export function getGlobPatternsFromPackageManagerWorkspaces(
250	  root: string,
251	  // allow overwriting these args so we can use them in devkit
252	  readJson: <T extends Object>(path: string) => T = <T extends Object>(
253	    path: string
254	  ) => readJsonFile<T>(join(root, path)),
255	  readYaml: <T extends Object>(path: string) => T = <T extends Object>(
256	    path: string
257	  ) => readYamlFile<T>(join(root, path)),
258	  exists: (path: string) => boolean = (p) => existsSync(join(root, p))
259	): string[] {
260	  try {
261	    const patterns: string[] = [];
262	    const packageJson = readJson<PackageJson>('package.json');
263	
264	    patterns.push(
265	      ...normalizePatterns(
266	        Array.isArray(packageJson.workspaces)
267	          ? packageJson.workspaces
268	          : packageJson.workspaces?.packages ?? []
269	      )
270	    );
271	
272	    if (exists('pnpm-workspace.yaml')) {
273	      try {
274	        const { packages } =
275	          readYaml<{ packages: string[] }>('pnpm-workspace.yaml') ?? {};
276	        patterns.push(...normalizePatterns(packages || []));
277	      } catch (e: unknown) {
278	        output.warn({
279	          title: `${NX_PREFIX} Unable to parse pnpm-workspace.yaml`,
280	          bodyLines: [e.toString()],
281	        });
282	      }
283	    }
284	
285	    if (existsSync(join(root, 'lerna.json'))) {
286	      try {
287	        const { packages } = readJson<any>('lerna.json') ?? {};
288	        patterns.push(
289	          ...normalizePatterns(packages?.length > 0 ? packages : ['packages/*'])
290	        );
291	      } catch (e: unknown) {
292	        output.warn({
293	          title: `${NX_PREFIX} Unable to parse lerna.json`,
294	          bodyLines: [e.toString()],
295	        });
296	      }
297	    }
298	
299	    // Merge patterns from workspaces definitions
300	    // TODO(@AgentEnder): update logic after better way to determine root project inclusion
301	    // Include the root project
302	    return packageJson.nx ? patterns.concat('package.json') : patterns;
303	  } catch {
304	    return [];
305	  }
306	}
307	
308	function normalizePatterns(patterns: string[]): string[] {
309	  return patterns.map((pattern) =>
310	    removeRelativePath(
311	      pattern.endsWith('/package.json')
312	        ? pattern
313	        : joinPathFragments(pattern, 'package.json')
314	    )
315	  );
316	}
317	
318	function removeRelativePath(pattern: string): string {
319	  return pattern.startsWith('./') ? pattern.substring(2) : pattern;
320	}
321	
322	function tryReadJson<T extends Object = any>(path: string): T | null {
323	  try {
324	    return readJsonFile<T>(path);
325	  } catch {
326	    return null;
327	  }
328	}
