import '../../internal-testing-utils/mock-fs';

import { vol } from 'memfs';
import { createNodeFromPackageJson, createNodesV2 } from './create-nodes';

describe('nx package.json workspaces plugin', () => {
  const context = {
    workspaceRoot: '/root',
    configFiles: [],
    nxJsonConfiguration: {},
  };

  afterEach(() => {
    vol.reset();
  });

  it('should build projects from package.json files', () => {
    vol.fromJSON(
      {
        'package.json': JSON.stringify({
          name: 'root',
          scripts: { echo: 'echo root project' },
        }),
        'packages/lib-a/package.json': JSON.stringify({
          name: 'lib-a',
          description: 'lib-a description',
          scripts: { test: 'jest' },
        }),
        'packages/lib-b/package.json': JSON.stringify({
          name: 'lib-b',
          description: 'lib-b description',
          scripts: {
            build: 'tsc',
            test: 'jest',
            nonNxOperation: 'rm -rf .',
          },
          nx: {
            implicitDependencies: ['lib-a'],
            includedScripts: ['build', 'test'],
            targets: {
              build: {
                outputs: ['{projectRoot}/dist'],
              },
            },
          },
        }),
      },
      '/root'
    );

    expect(createNodeFromPackageJson('package.json', '/root', {}, false))
      .toMatchInlineSnapshot(`
      {
        "projects": {
          ".": {
            "metadata": {
              "description": undefined,
              "js": {
                "isInPackageManagerWorkspaces": false,
                "packageExports": undefined,
                "packageMain": undefined,
                "packageName": "root",
              },
              "targetGroups": {
                "NPM Scripts": [
                  "echo",
                ],
              },
            },
            "name": "root",
            "root": ".",
            "tags": [
              "npm:public",
            ],
            "targets": {
              "echo": {
                "executor": "nx:run-script",
                "metadata": {
                  "runCommand": "npm run echo",
                  "scriptContent": "echo root project",
                },
                "options": {
                  "script": "echo",
                },
              },
              "nx-release-publish": {
                "dependsOn": [
                  "^nx-release-publish",
                ],
                "executor": "@nx/js:release-publish",
                "options": {},
              },
            },
          },
        },
      }
    `);
    expect(
      createNodeFromPackageJson(
        'packages/lib-a/package.json',
        '/root',
        {},
        false
      )
    ).toMatchInlineSnapshot(`
      {
        "projects": {
          "packages/lib-a": {
            "metadata": {
              "description": "lib-a description",
              "js": {
                "isInPackageManagerWorkspaces": false,
                "packageExports": undefined,
                "packageMain": undefined,
                "packageName": "lib-a",
              },
              "targetGroups": {
                "NPM Scripts": [
                  "test",
                ],
              },
            },
            "name": "lib-a",
            "root": "packages/lib-a",
            "tags": [
              "npm:public",
            ],
            "targets": {
              "nx-release-publish": {
                "dependsOn": [
                  "^nx-release-publish",
                ],
                "executor": "@nx/js:release-publish",
                "options": {},
              },
              "test": {
                "executor": "nx:run-script",
                "metadata": {
                  "runCommand": "npm run test",
                  "scriptContent": "jest",
                },
                "options": {
                  "script": "test",
                },
              },
            },
          },
        },
      }
    `);
    expect(
      createNodeFromPackageJson(
        'packages/lib-b/package.json',
        '/root',
        {},
        false
      )
    ).toMatchInlineSnapshot(`
      {
        "projects": {
          "packages/lib-b": {
            "implicitDependencies": [
              "lib-a",
            ],
            "includedScripts": [
              "build",
              "test",
            ],
            "metadata": {
              "description": "lib-b description",
              "js": {
                "isInPackageManagerWorkspaces": false,
                "packageExports": undefined,
                "packageMain": undefined,
                "packageName": "lib-b",
              },
              "targetGroups": {
                "NPM Scripts": [
                  "build",
                  "test",
                ],
              },
            },
            "name": "lib-b",
            "root": "packages/lib-b",
            "tags": [
              "npm:public",
            ],
            "targets": {
              "build": {
                "executor": "nx:run-script",
                "metadata": {
                  "runCommand": "npm run build",
                  "scriptContent": "tsc",
                },
                "options": {
                  "script": "build",
                },
                "outputs": [
                  "{projectRoot}/dist",
                ],
              },
              "nx-release-publish": {
                "dependsOn": [
                  "^nx-release-publish",
                ],
                "executor": "@nx/js:release-publish",
                "options": {},
              },
              "test": {
                "executor": "nx:run-script",
                "metadata": {
                  "runCommand": "npm run test",
                  "scriptContent": "jest",
                },
                "options": {
                  "script": "test",
                },
              },
            },
          },
        },
      }
    `);
  });

  describe('negative patterns', () => {
    it('should work based on negative patterns defined in package.json workspaces', async () => {
      vol.fromJSON(
        {
          'package.json': JSON.stringify({
            name: 'root',
            workspaces: [
              'packages/*',
              // Multiple negative entries
              '!packages/fs',
              '!packages/orm-browser-example',
              '!packages/framework-examples',
            ],
          }),
          'packages/vite/package.json': JSON.stringify({ name: 'vite' }),
          'packages/fs/package.json': JSON.stringify({ name: 'fs' }),
          'packages/orm-browser-example/package.json': JSON.stringify({
            name: 'orm-browser-example',
          }),
          'packages/framework-examples/package.json': JSON.stringify({
            name: 'framework-examples',
          }),
        },
        '/root'
      );

      const context = {
        workspaceRoot: '/root',
        configFiles: [],
        nxJsonConfiguration: {},
      };

      // No matching project based on the package.json "workspace" config
      expect(
        await createNodesV2[1](['package.json'], undefined, context)
      ).toMatchInlineSnapshot(`[]`);

      // Matching project based on the package.json "workspace" config
      expect(
        await createNodesV2[1](
          ['packages/vite/package.json'],
          undefined,
          context
        )
      ).toMatchInlineSnapshot(`
        [
          [
            "packages/vite/package.json",
            {
              "projects": {
                "packages/vite": {
                  "metadata": {
                    "description": undefined,
                    "js": {
                      "isInPackageManagerWorkspaces": true,
                      "packageExports": undefined,
                      "packageMain": undefined,
                      "packageName": "vite",
                    },
                    "targetGroups": {},
                  },
                  "name": "vite",
                  "root": "packages/vite",
                  "tags": [
                    "npm:public",
                  ],
                  "targets": {
                    "nx-release-publish": {
                      "dependsOn": [
                        "^nx-release-publish",
                      ],
                      "executor": "@nx/js:release-publish",
                      "options": {},
                    },
                  },
                },
              },
            },
          ],
        ]
      `);

      // No matching project based on the package.json "workspace" config
      expect(
        await createNodesV2[1](['packages/fs/package.json'], undefined, context)
      ).toMatchInlineSnapshot(`[]`);

      // No matching project based on the package.json "workspace" config
      expect(
