  1	import {
  2	  chmodSync,
  3	  mkdirSync,
  4	  readdirSync,
  5	  readFileSync,
  6	  rmSync,
  7	  statSync,
  8	  writeFileSync,
  9	} from 'node:fs';
 10	import type { Mode } from 'node:fs';
 11	import { logger } from '../utils/logger';
 12	import { output } from '../utils/output';
 13	import { dirname, join, relative, sep } from 'path';
 14	import * as chalk from 'chalk';
 15	
 16	/**
 17	 * Options to set when writing a file in the Virtual file system tree.
 18	 */
 19	export interface TreeWriteOptions {
 20	  /**
 21	   * Permission to be granted on the file, given as a string (e.g `755`) or octal integer (e.g `0o755`).
 22	   * The logical OR operator can be used to separate multiple permissions.
 23	   * See https://nodejs.org/api/fs.html#fs_file_modes
 24	   */
 25	  mode?: Mode;
 26	}
 27	
 28	/**
 29	 * Virtual file system tree.
 30	 */
 31	export interface Tree {
 32	  /**
 33	   * Root of the workspace. All paths are relative to this.
 34	   */
 35	  root: string;
 36	
 37	  /**
 38	   * Read the contents of a file.
 39	   * @param filePath A path to a file.
 40	   */
 41	  read(filePath: string): Buffer | null;
 42	
 43	  /**
 44	   * Read the contents of a file as string.
 45	   * @param filePath A path to a file.
 46	   * @param encoding the encoding for the result
 47	   */
 48	  read(filePath: string, encoding: BufferEncoding): string | null;
 49	
 50	  /**
 51	   * Update the contents of a file or create a new file.
 52	   */
 53	  write(
 54	    filePath: string,
 55	    content: Buffer | string,
 56	    options?: TreeWriteOptions
 57	  ): void;
 58	
 59	  /**
 60	   * Check if a file exists.
 61	   */
 62	  exists(filePath: string): boolean;
 63	
 64	  /**
 65	   * Delete the file.
 66	   */
 67	  delete(filePath: string): void;
 68	
 69	  /**
 70	   * Rename the file or the folder.
 71	   */
 72	  rename(from: string, to: string): void;
 73	
 74	  /**
 75	   * Check if this is a file or not.
 76	   */
 77	  isFile(filePath: string): boolean;
 78	
 79	  /**
 80	   * Returns the list of children of a folder.
 81	   */
 82	  children(dirPath: string): string[];
 83	
 84	  /**
 85	   * Returns the list of currently recorded changes.
 86	   */
 87	  listChanges(): FileChange[];
 88	
 89	  /**
 90	   * Changes permissions of a file.
 91	   * @param filePath A path to a file.
 92	   * @param mode The permission to be granted on the file, given as a string (e.g `755`) or octal integer (e.g `0o755`).
 93	   * See https://nodejs.org/api/fs.html#fs_file_modes.
 94	   */
 95	  changePermissions(filePath: string, mode: Mode): void;
 96	}
 97	
 98	/**
 99	 * Description of a file change in the Nx virtual file system/
100	 */
101	export interface FileChange {
102	  /**
103	   * Path relative to the workspace root
104	   */
105	  path: string;
106	
107	  /**
108	   * Type of change: 'CREATE' | 'DELETE' | 'UPDATE'
109	   */
110	  type: 'CREATE' | 'DELETE' | 'UPDATE';
111	
112	  /**
113	   * The content of the file or null in case of delete.
114	   */
115	  content: Buffer | null;
116	  /**
117	   * Options to set on the file being created or updated.
118	   */
119	  options?: TreeWriteOptions;
120	}
121	
122	export class FsTree implements Tree {
123	  private recordedChanges: {
124	    [path: string]: {
125	      content: Buffer | null;
126	      isDeleted: boolean;
127	      options?: TreeWriteOptions;
128	    };
129	  } = {};
130	
131	  /**
132	   * Signifies if operations on the tree instance
133	   * are allowed. Set to false after changes are written
134	   * to disk, to prevent someone trying to use the tree to update
135	   * files when the tree is no longer effective.
136	   */
137	  private locked = false;
138	
139	  constructor(
140	    readonly root: string,
141	    private readonly isVerbose: boolean,
142	    private readonly logOperationId?: string
143	  ) {}
144	
145	  read(filePath: string): Buffer | null;
146	  read(filePath: string, encoding: BufferEncoding): string | null;
147	  read(filePath: string, encoding?: BufferEncoding): Buffer | string | null {
148	    filePath = this.normalize(filePath);
149	    try {
150	      let content: Buffer;
151	      if (this.recordedChanges[this.rp(filePath)]) {
152	        content = this.recordedChanges[this.rp(filePath)].content;
153	      } else {
154	        content = this.fsReadFile(filePath);
155	      }
156	
157	      return encoding ? content.toString(encoding) : content;
158	    } catch (e) {
159	      if (this.isVerbose) {
160	        logger.error(e);
161	      }
162	      return null;
163	    }
164	  }
165	
166	  write(
167	    filePath: string,
168	    content: Buffer | string,
169	    options?: TreeWriteOptions
170	  ): void {
171	    this.assertUnlocked();
172	    filePath = this.normalize(filePath);
173	
174	    // Remove any recorded changes where a parent directory has been
175	    // deleted when writing a new file within the directory.
176	    let parent = dirname(this.rp(filePath));
177	    while (parent !== '.') {
178	      if (this.recordedChanges[parent]?.isDeleted) {
179	        delete this.recordedChanges[parent];
180	      }
181	      parent = dirname(parent);
182	    }
183	
184	    if (
185	      this.fsExists(this.rp(filePath)) &&
186	      Buffer.from(content).equals(this.fsReadFile(filePath))
187	    ) {
188	      // Remove recorded change because the file has been restored to it's original contents
189	      delete this.recordedChanges[this.rp(filePath)];
190	      return;
191	    }
192	
193	    try {
194	      this.recordedChanges[this.rp(filePath)] = {
195	        content: Buffer.from(content),
196	        isDeleted: false,
197	        options,
198	      };
199	    } catch (e) {
200	      if (this.isVerbose) {
201	        logger.error(e);
202	      }
203	    }
204	  }
205	
206	  overwrite(
207	    filePath: string,
208	    content: Buffer | string,
209	    options?: TreeWriteOptions
210	  ): void {
211	    filePath = this.normalize(filePath);
212	    this.write(filePath, content, options);
213	  }
214	
215	  delete(filePath: string): void {
216	    this.assertUnlocked();
217	    filePath = this.normalize(filePath);
218	    if (this.filesForDir(this.rp(filePath)).length > 0) {
219	      this.filesForDir(this.rp(filePath)).forEach(
220	        (f) => (this.recordedChanges[f] = { content: null, isDeleted: true })
221	      );
222	    }
223	    this.recordedChanges[this.rp(filePath)] = {
224	      content: null,
225	      isDeleted: true,
226	    };
227	
228	    // Delete directory when is not root and there are no children
229	    if (
230	      filePath !== '' &&
231	      this.children(dirname(this.rp(filePath))).length < 1
232	    ) {
233	      this.delete(dirname(this.rp(filePath)));
234	    }
235	  }
236	
237	  exists(filePath: string): boolean {
238	    filePath = this.normalize(filePath);
239	    try {
240	      if (this.recordedChanges[this.rp(filePath)]) {
241	        return !this.recordedChanges[this.rp(filePath)].isDeleted;
242	      } else if (this.filesForDir(this.rp(filePath)).length > 0) {
243	        return true;
244	      } else {
245	        return this.fsExists(filePath);
246	      }
247	    } catch {
248	      return false;
249	    }
250	  }
251	
252	  rename(from: string, to: string): void {
253	    this.assertUnlocked();
254	    from = this.normalize(from);
255	    to = this.normalize(to);
256	    if (from === to) {
257	      return;
258	    }
259	
260	    if (this.isFile(from)) {
261	      const content = this.read(this.rp(from));
262	      this.write(this.rp(to), content);
263	      this.delete(this.rp(from));
264	    } else {
265	      for (const child of this.children(from)) {
266	        this.rename(join(from, child), join(to, child));
267	      }
268	    }
269	  }
270	
271	  isFile(filePath: string): boolean {
272	    filePath = this.normalize(filePath);
273	    try {
274	      if (this.recordedChanges[this.rp(filePath)]) {
275	        return !this.recordedChanges[this.rp(filePath)].isDeleted;
276	      } else {
277	        return this.fsIsFile(filePath);
278	      }
279	    } catch {
280	      return false;
281	    }
282	  }
283	
284	  children(dirPath: string): string[] {
285	    dirPath = this.normalize(dirPath);
286	    let res = this.fsReadDir(dirPath);
287	
288	    res = [...res, ...this.directChildrenOfDir(this.rp(dirPath))];
289	    res = res.filter((q) => {
290	      const r = this.recordedChanges[this.normalize(join(this.rp(dirPath), q))];
291	      return !r?.isDeleted;
292	    });
293	    // Dedupe
294	    return Array.from(new Set(res));
295	  }
296	
297	  listChanges(): FileChange[] {
298	    const res = [] as FileChange[];
299	    Object.keys(this.recordedChanges).forEach((f) => {
300	      if (this.recordedChanges[f].isDeleted) {
301	        if (this.fsExists(f)) {
302	          res.push({ path: f, type: 'DELETE', content: null });
303	        }
304	      } else {
305	        if (this.fsExists(f)) {
306	          res.push({
307	            path: f,
308	            type: 'UPDATE',
309	            content: this.recordedChanges[f].content,
310	            options: this.recordedChanges[f].options,
311	          });
312	        } else {
313	          res.push({
314	            path: f,
315	            type: 'CREATE',
316	            content: this.recordedChanges[f].content,
317	            options: this.recordedChanges[f].options,
318	          });
319	        }
320	      }
321	    });
322	    return res;
323	  }
324	
325	  changePermissions(filePath: string, mode: Mode): void {
326	    this.assertUnlocked();
327	    filePath = this.normalize(filePath);
328	    const filePathChangeKey = this.rp(filePath);
329	    if (this.recordedChanges[filePathChangeKey]) {
330	      if (this.recordedChanges[filePathChangeKey].isDeleted) {
331	        throw new Error(
332	          `Cannot change permissions of deleted file ${filePath}.`
333	        );
334	      }
335	
336	      this.recordedChanges[filePathChangeKey].options = { mode };
337	    } else if (!this.fsExists(filePath)) {
338	      throw new Error(
339	        `Cannot change permissions of non-existing file ${filePath}.`
340	      );
341	    } else if (!this.fsIsFile(filePath)) {
342	      // To fully support directories we'd need to change how we store
343	      // changes to keep a record of directories so we can associate
344	      // permissions to them.
345	      throw new Error(`Cannot change permissions of non-file ${filePath}.`);
346	    } else {
347	      this.recordedChanges[filePathChangeKey] = {
348	        content: this.fsReadFile(filePath),
349	        isDeleted: false,
350	        options: { mode },
351	      };
352	    }
353	  }
354	
355	  // Marks FsTree as final.
356	  lock() {
357	    this.locked = true;
358	  }
359	
360	  private assertUnlocked() {
361	    if (this.locked) {
362	      output.error({
363	        title: `File changes have already been written to disk. Further changes were attempted ${
364	          this.logOperationId ? ` while running ${this.logOperationId}.` : '.'
365	        }`,
366	        bodyLines: [
367	          'The file system can no longer be modified. This commonly happens when a generator attempts to make further changes in its callback, or an asynchronous operation is still running after the generator completes.',
368	        ],
369	      });
370	      throw new Error('Tree changed after commit to disk.');
371	    }
372	  }
373	
374	  private normalize(path: string) {
375	    return relative(this.root, join(this.root, path)).split(sep).join('/');
376	  }
377	
378	  private fsReadDir(dirPath: string) {
379	    try {
380	      return readdirSync(join(this.root, dirPath));
381	    } catch {
382	      return [];
383	    }
384	  }
385	
386	  private fsIsFile(filePath: string): boolean {
387	    const stat = statSync(join(this.root, filePath));
388	    return stat.isFile();
389	  }
390	
391	  private fsReadFile(filePath: string): Buffer {
392	    return readFileSync(join(this.root, filePath));
393	  }
394	
395	  private fsExists(filePath: string): boolean {
396	    try {
397	      const stat = statSync(join(this.root, filePath));
398	      return stat.isFile() || stat.isDirectory();
399	    } catch {
400	      return false;
401	    }
402	  }
403	
404	  private filesForDir(path: string): string[] {
405	    return Object.keys(this.recordedChanges).filter(
406	      (f) => f.startsWith(`${path}/`) && !this.recordedChanges[f].isDeleted
407	    );
408	  }
409	
410	  private directChildrenOfDir(path: string): string[] {
411	    const res = {};
412	    if (path === '') {
413	      return Object.keys(this.recordedChanges).map(
414	        (file) => file.split('/')[0]
415	      );
416	    }
417	    Object.keys(this.recordedChanges).forEach((f) => {
418	      if (f.startsWith(`${path}/`)) {
419	        // Remove the current folder's path from the directory
420	        const file = f.substring(path.length + 1);
421	        // Split the path on segments, and take the first one
422	        const basePath = file.split('/')[0];
423	        // Mark it as a child of the current directory
424	        res[basePath] = true;
425	      }
426	    });
427	
428	    return Object.keys(res);
429	  }
430	
431	  private rp(pp: string): string {
432	    return pp.startsWith('/') ? pp.substring(1) : pp;
433	  }
434	}
435	
436	export function flushChanges(root: string, fileChanges: FileChange[]): void {
437	  fileChanges.forEach((f) => {
438	    const fpath = join(root, f.path);
439	    if (f.type === 'CREATE') {
440	      mkdirSync(dirname(fpath), { recursive: true });
441	      writeFileSync(fpath, f.content);
442	      if (f.options?.mode) chmodSync(fpath, f.options.mode);
443	    } else if (f.type === 'UPDATE') {
444	      writeFileSync(fpath, f.content);
445	      if (f.options?.mode) chmodSync(fpath, f.options.mode);
446	    } else if (f.type === 'DELETE') {
447	      rmSync(fpath, { recursive: true, force: true });
448	    }
449	  });
450	}
451	
452	export function printChanges(
453	  fileChanges: FileChange[],
454	  indent: string = ''
455	): void {
456	  fileChanges.forEach((f) => {
457	    if (f.type === 'CREATE') {
458	      console.log(`${indent}${chalk.green('CREATE')} ${f.path}`);
459	    } else if (f.type === 'UPDATE') {
460	      console.log(`${indent}${chalk.white('UPDATE')} ${f.path}`);
461	    } else if (f.type === 'DELETE') {
462	      console.log(`${indent}${chalk.yellow('DELETE')} ${f.path}`);
463	    }
464	  });
465	}
