143	  enabled() {
144	    if (this._enabled === undefined) {
145	      const useDaemonProcessOption = this.nxJson?.useDaemonProcess;
146	      const env = process.env.NX_DAEMON;
147	
148	      // env takes precedence
149	      // option=true,env=false => no daemon
150	      // option=false,env=undefined => no daemon
151	      // option=false,env=false => no daemon
152	
153	      // option=undefined,env=undefined => daemon
154	      // option=true,env=true => daemon
155	      // option=false,env=true => daemon
156	
157	      // CI=true,env=undefined => no daemon
158	      // CI=true,env=false => no daemon
159	      // CI=true,env=true => daemon
160	
161	      // docker=true,env=undefined => no daemon
162	      // docker=true,env=false => no daemon
163	      // docker=true,env=true => daemon
164	      // WASM => no daemon because file watching does not work
165	      if (
166	        ((isCI() || isDocker()) && env !== 'true') ||
167	        isDaemonDisabled() ||
168	        nxJsonIsNotPresent() ||
169	        (useDaemonProcessOption === undefined && env === 'false') ||
170	        (useDaemonProcessOption === true && env === 'false') ||
171	        (useDaemonProcessOption === false && env === undefined) ||
172	        (useDaemonProcessOption === false && env === 'false')
173	      ) {
174	        this._enabled = false;
175	      } else if (IS_WASM) {
176	        output.warn({
177	          title:
178	            'The Nx Daemon is unsupported in WebAssembly environments. Some things may be slower than or not function as expected.',
179	        });
180	        this._enabled = false;
181	      } else {
182	        this._enabled = true;
183	      }
184	    }
185	    return this._enabled;
186	  }
