188	  reset() {
189	    this.socketMessenger?.close();
190	    this.socketMessenger = null;
191	    this.queue = new PromisedBasedQueue();
192	    this.currentMessage = null;
193	    this.currentResolve = null;
194	    this.currentReject = null;
195	    this._enabled = undefined;
196	
197	    this._out?.close();
198	    this._err?.close();
199	    this._out = null;
200	    this._err = null;
201	
202	    this._daemonStatus = DaemonStatus.DISCONNECTED;
203	    this._waitForDaemonReady = new Promise<void>(
204	      (resolve) => (this._daemonReady = resolve)
205	    );
206	  }
