212	  async getProjectGraphAndSourceMaps(): Promise<{
213	    projectGraph: ProjectGraph;
214	    sourceMaps: ConfigurationSourceMaps;
215	  }> {
216	    let spinner: DelayedSpinner;
217	    // If the graph takes a while to load, we want to show a spinner.
218	    spinner = new DelayedSpinner(
219	      'Calculating the project graph on the Nx Daemon'
220	    ).scheduleMessageUpdate(
221	      'Calculating the project graph on the Nx Daemon is taking longer than expected. Re-run with NX_DAEMON=false to see more details.',
222	      { ciDelay: 60_000, delay: 30_000 }
223	    );
224	    try {
225	      const response = await this.sendToDaemonViaQueue({
226	        type: 'REQUEST_PROJECT_GRAPH',
227	      });
228	      return {
229	        projectGraph: response.projectGraph,
230	        sourceMaps: response.sourceMaps,
231	      };
232	    } catch (e) {
233	      if (e.name === DaemonProjectGraphError.name) {
234	        throw ProjectGraphError.fromDaemonProjectGraphError(e);
235	      } else {
236	        throw e;
237	      }
238	    } finally {
