263	    config: {
264	      watchProjects: string[] | 'all';
265	      includeGlobalWorkspaceFiles?: boolean;
266	      includeDependentProjects?: boolean;
267	      allowPartialGraph?: boolean;
268	    },
269	    callback: (
270	      error: Error | null | 'closed',
271	      data: {
272	        changedProjects: string[];
273	        changedFiles: ChangedFile[];
274	      } | null
275	    ) => void
