284	      }
285	    }
286	    let messenger: DaemonSocketMessenger | undefined;
287	
288	    await this.queue.sendToQueue(() => {
289	      messenger = new DaemonSocketMessenger(
290	        connect(getFullOsSocketPath())
291	      ).listen(
292	        (message) => {
293	          try {
294	            const parsedMessage = JSON.parse(message);
295	            callback(null, parsedMessage);
296	          } catch (e) {
297	            callback(e, null);
298	          }
299	        },
300	        () => {
301	          callback('closed', null);
302	        },
303	        (err) => callback(err, null)
304	      );
305	      return messenger.sendMessage({ type: 'REGISTER_FILE_WATCHER', config });
306	    });
307	
308	    return () => {
309	      messenger?.close();
310	    };
311	  }
