509	  private async sendToDaemonViaQueue(messageToDaemon: Message): Promise<any> {
510	    return this.queue.sendToQueue(() =>
511	      this.sendMessageToDaemon(messageToDaemon)
512	    );
513	  }
514	
515	  private setUpConnection() {
516	    this.socketMessenger = new DaemonSocketMessenger(
517	      connect(getFullOsSocketPath())
518	    ).listen(
519	      (message) => this.handleMessage(message),
520	      () => {
521	        // it's ok for the daemon to terminate if the client doesn't wait on
522	        // any messages from the daemon
523	        if (this.queue.isEmpty()) {
524	          this.reset();
525	        } else {
526	          output.error({
527	            title: 'Daemon process terminated and closed the connection',
528	            bodyLines: [
529	              'Please rerun the command, which will restart the daemon.',
530	              `If you get this error again, check for any errors in the daemon process logs found in: ${DAEMON_OUTPUT_LOG_FILE}`,
531	            ],
532	          });
533	          this._daemonStatus = DaemonStatus.DISCONNECTED;
534	          this.currentReject?.(
535	            daemonProcessException(
536	              'Daemon process terminated and closed the connection'
537	            )
538	          );
539	          process.exit(1);
540	        }
541	      },
542	      (err) => {
543	        if (!err.message) {
544	          return this.currentReject(daemonProcessException(err.toString()));
545	        }
546	
547	        if (err.message.startsWith('LOCK-FILES-CHANGED')) {
548	          // retry the current message
549	          // we cannot send it via the queue because we are in the middle of processing
550	          // a message from the queue
551	          return this.sendMessageToDaemon(this.currentMessage).then(
552	            this.currentResolve,
553	            this.currentReject
554	          );
555	        }
556	
557	        let error: any;
558	        if (err.message.startsWith('connect ENOENT')) {
559	          error = daemonProcessException('The Daemon Server is not running');
560	        } else if (err.message.startsWith('connect ECONNREFUSED')) {
561	          error = daemonProcessException(
562	            `A server instance had not been fully shut down. Please try running the command again.`
563	          );
564	          killSocketOrPath();
565	        } else if (err.message.startsWith('read ECONNRESET')) {
566	          error = daemonProcessException(
567	            `Unable to connect to the daemon process.`
568	          );
569	        } else {
570	          error = daemonProcessException(err.toString());
571	        }
572	        return this.currentReject(error);
573	      }
574	    );
575	  }
576	
577	  private async sendMessageToDaemon(message: Message): Promise<any> {
578	    if (this._daemonStatus == DaemonStatus.DISCONNECTED) {
579	      this._daemonStatus = DaemonStatus.CONNECTING;
580	
581	      if (!(await this.isServerAvailable())) {
582	        await this.startInBackground();
583	      }
584	      this.setUpConnection();
585	      this._daemonStatus = DaemonStatus.CONNECTED;
586	      this._daemonReady();
587	    } else if (this._daemonStatus == DaemonStatus.CONNECTING) {
588	      await this._waitForDaemonReady;
589	    }
590	    // An open promise isn't enough to keep the event loop
591	    // alive, so we set a timeout here and clear it when we hear
592	    // back
593	    const keepAlive = setTimeout(() => {}, 10 * 60 * 1000);
594	    return new Promise((resolve, reject) => {
595	      performance.mark('sendMessageToDaemon-start');
596	
597	      this.currentMessage = message;
598	      this.currentResolve = resolve;
599	      this.currentReject = reject;
600	
601	      this.socketMessenger.sendMessage(message);
602	    }).finally(() => {
603	      clearTimeout(keepAlive);
604	    });
605	  }
606	
607	  private handleMessage(serializedResult: string) {
608	    try {
609	      performance.mark('json-parse-start');
610	      const parsedResult = JSON.parse(serializedResult);
611	      performance.mark('json-parse-end');
612	      performance.measure(
613	        'deserialize daemon response',
614	        'json-parse-start',
615	        'json-parse-end'
616	      );
617	      if (parsedResult.error) {
618	        this.currentReject(parsedResult.error);
619	      } else {
620	        performance.measure(
621	          'total for sendMessageToDaemon()',
622	          'sendMessageToDaemon-start',
623	          'json-parse-end'
624	        );
625	        return this.currentResolve(parsedResult);
626	      }
627	    } catch (e) {
628	      const endOfResponse =
629	        serializedResult.length > 300
630	          ? serializedResult.substring(serializedResult.length - 300)
631	          : serializedResult;
632	      this.currentReject(
633	        daemonProcessException(
634	          [
635	            'Could not deserialize response from Nx daemon.',
636	            `Message: ${e.message}`,
637	            '\n',
638	            `Received:`,
639	            endOfResponse,
640	            '\n',
641	          ].join('\n')
642	        )
643	      );
644	    }
645	  }
646	
647	  async startInBackground(): Promise<ChildProcess['pid']> {
648	    if (global.NX_PLUGIN_WORKER) {
649	      throw new Error(
650	        'Fatal Error: Something unexpected has occurred. Plugin Workers should not start a new daemon process. Please report this issue.'
651	      );
652	    }
653	
654	    mkdirSync(DAEMON_DIR_FOR_CURRENT_WORKSPACE, { recursive: true });
655	    if (!existsSync(DAEMON_OUTPUT_LOG_FILE)) {
656	      writeFileSync(DAEMON_OUTPUT_LOG_FILE, '');
657	    }
658	
659	    this._out = await open(DAEMON_OUTPUT_LOG_FILE, 'a');
660	    this._err = await open(DAEMON_OUTPUT_LOG_FILE, 'a');
661	
662	    const backgroundProcess = spawn(
663	      process.execPath,
664	      [join(__dirname, `../server/start.js`)],
665	      {
666	        cwd: workspaceRoot,
667	        stdio: ['ignore', this._out.fd, this._err.fd],
668	        detached: true,
669	        windowsHide: false,
670	        shell: false,
671	        env: {
672	          ...process.env,
673	          ...DAEMON_ENV_SETTINGS,
674	        },
675	      }
676	    );
677	    backgroundProcess.unref();
678	
679	    /**
680	     * Ensure the server is actually available to connect to via IPC before resolving
681	     */
682	    let attempts = 0;
683	    return new Promise((resolve, reject) => {
684	      const id = setInterval(async () => {
685	        if (await this.isServerAvailable()) {
686	          clearInterval(id);
687	          resolve(backgroundProcess.pid);
688	        } else if (attempts > 6000) {
689	          // daemon fails to start, the process probably exited
690	          // we print the logs and exit the client
691	          reject(
692	            daemonProcessException(
693	              'Failed to start or connect to the Nx Daemon process.'
694	            )
695	          );
696	        } else {
697	          attempts++;
698	        }
699	      }, 10);
700	    });
701	  }
