703	  async stop(): Promise<void> {
704	    try {
705	      await this.sendMessageToDaemon({ type: FORCE_SHUTDOWN });
706	      await waitForDaemonToExitAndCleanupProcessJson();
707	    } catch (err) {
708	      output.error({
709	        title:
710	          err?.message ||
711	          'Something unexpected went wrong when stopping the daemon server',
712	      });
713	    }
714	
715	    removeSocketDir();
716	  }
717	}
718	
719	export const daemonClient = new DaemonClient();
720	
721	export function isDaemonEnabled() {
