282	function hashDirectory(dir: string): string {
283	  const files = readdirSync(dir).sort();
284	  const hashes = files.map((file) => {
285	    const filePath = join(dir, file);
286	    const stat = statSync(filePath);
287	
288	    // If the current path is a directory, recursively hash the contents
289	    if (stat.isDirectory()) {
290	      return hashDirectory(filePath);
291	    }
292	
293	    // If it's a file, hash the file contents
294	    const content = readFileSync(filePath);
295	    return createHash('sha256').update(content).digest('hex');
296	  });
297	
298	  // Hash the combined hashes of the directory's contents
299	  const combinedHashes = hashes.sort().join('');
300	  return createHash('sha256').update(combinedHashes).digest('hex');
