308	): Promise<string> {
309	  let resp;
310	  try {
311	    resp = await axios.get(url, {
312	      responseType: 'stream',
313	    } as AxiosRequestConfig);
314	  } catch (e: any) {
315	    console.error('Error while updating Nx Cloud client bundle');
316	    throw e;
317	  }
