319	  const bundleExtractLocation = join(runnerBundleInstallDirectory, version);
320	
321	  if (!existsSync(bundleExtractLocation)) {
322	    mkdirSync(bundleExtractLocation);
323	  }
324	  return new Promise((res, rej) => {
325	    const extract = tar.extract();
326	    extract.on('entry', function (headers, stream, next) {
327	      if (headers.type === 'directory') {
328	        const directoryPath = join(bundleExtractLocation, headers.name);
329	        if (!existsSync(directoryPath)) {
330	          mkdirSync(directoryPath, { recursive: true });
331	        }
332	        next();
333	
334	        stream.resume();
335	      } else if (headers.type === 'file') {
336	        const outputFilePath = join(bundleExtractLocation, headers.name);
337	        const writeStream = createWriteStream(outputFilePath);
338	        stream.pipe(writeStream);
339	
340	        // Continue the tar stream after the write stream closes
341	        writeStream.on('close', () => {
342	          next();
343	        });
344	
345	        stream.resume();
346	      }
347	    });
348	
349	    extract.on('error', (e) => {
350	      rej(e);
351	    });
352	
353	    extract.on('finish', function () {
354	      removeOldClientBundles(version);
355	      writeBundleVerificationLock();
356	      res(bundleExtractLocation);
357	    });
358	
359	    resp.data.pipe(createGunzip()).pipe(extract);
360	  });
361	}
