  1	import { calculateFileChanges } from '../../project-graph/file-utils';
  2	import { runCommand } from '../../tasks-runner/run-command';
  3	import { output } from '../../utils/output';
  4	import { connectToNxCloudIfExplicitlyAsked } from '../connect/connect-to-nx-cloud';
  5	import type { NxArgs } from '../../utils/command-line-utils';
  6	import {
  7	  parseFiles,
  8	  readGraphFileFromGraphArg,
  9	  splitArgsIntoNxArgsAndOverrides,
 10	} from '../../utils/command-line-utils';
 11	import { performance } from 'perf_hooks';
 12	import { createProjectGraphAsync } from '../../project-graph/project-graph';
 13	import {
 14	  ProjectGraph,
 15	  ProjectGraphProjectNode,
 16	} from '../../config/project-graph';
 17	import { projectHasTarget } from '../../utils/project-graph-utils';
 18	import { filterAffected } from '../../project-graph/affected/affected-project-graph';
 19	import { TargetDependencyConfig } from '../../config/workspace-json-project-json';
 20	import { readNxJson } from '../../config/configuration';
 21	import { findMatchingProjects } from '../../utils/find-matching-projects';
 22	import { generateGraph } from '../graph/graph';
 23	import { allFileData } from '../../utils/all-file-data';
 24	
 25	export async function affected(
 26	  command: 'graph' | 'print-affected' | 'affected',
 27	  args: { [k: string]: any },
 28	  extraTargetDependencies: Record<
 29	    string,
 30	    (TargetDependencyConfig | string)[]
 31	  > = {},
 32	  extraOptions = {
 33	    excludeTaskDependencies: args.excludeTaskDependencies,
 34	    loadDotEnvFiles: process.env.NX_LOAD_DOT_ENV_FILES !== 'false',
 35	  } as {
 36	    excludeTaskDependencies: boolean;
 37	    loadDotEnvFiles: boolean;
 38	  }
 39	): Promise<void> {
 40	  performance.mark('code-loading:end');
 41	  performance.measure('code-loading', 'init-local', 'code-loading:end');
 42	
 43	  const nxJson = readNxJson();
 44	  const { nxArgs, overrides } = splitArgsIntoNxArgsAndOverrides(
 45	    args,
 46	    'affected',
 47	    {
 48	      printWarnings:
 49	        command !== 'print-affected' && !args.plain && args.graph !== 'stdout',
 50	    },
 51	    nxJson
 52	  );
 53	
 54	  await connectToNxCloudIfExplicitlyAsked(nxArgs);
 55	
 56	  const projectGraph = await createProjectGraphAsync({
 57	    exitOnError: true,
 58	  });
 59	  const projects = await getAffectedGraphNodes(nxArgs, projectGraph);
 60	
 61	  try {
 62	    switch (command) {
 63	      case 'affected': {
 64	        const projectsWithTarget = allProjectsWithTarget(projects, nxArgs);
 65	        if (nxArgs.graph) {
 66	          const projectNames = projectsWithTarget.map((t) => t.name);
 67	          const file = readGraphFileFromGraphArg(nxArgs);
 68	
 69	          return await generateGraph(
 70	            {
 71	              watch: true,
 72	              open: true,
 73	              view: 'tasks',
 74	              targets: nxArgs.targets,
 75	              projects: projectNames,
 76	              file,
 77	            },
 78	            projectNames
 79	          );
