  1	import * as yargsParser from 'yargs-parser';
  2	import { ExecutorContext } from '../../config/misc-interfaces';
  3	import { isTuiEnabled } from '../../tasks-runner/is-tui-enabled';
  4	import { PseudoTerminal } from '../../tasks-runner/pseudo-terminal';
  5	import {
  6	  ParallelRunningTasks,
  7	  runSingleCommandWithPseudoTerminal,
  8	  SeriallyRunningTasks,
  9	} from './running-tasks';
 10	
 11	export const LARGE_BUFFER = 1024 * 1000000;
 12	export type Json = {
 13	  [k: string]: any;
 14	};
 15	
 16	export interface RunCommandsCommandOptions {
 17	  command: string;
 18	  forwardAllArgs?: boolean;
 19	  /**
 20	   * description was added to allow users to document their commands inline,
 21	   * it is not intended to be used as part of the execution of the command.
 22	   */
 23	  description?: string;
 24	  prefix?: string;
 25	  prefixColor?: string;
 26	  color?: string;
 27	  bgColor?: string;
 28	}
 29	
 30	export interface RunCommandsOptions extends Json {
 31	  command?: string | string[];
 32	  commands?: Array<RunCommandsCommandOptions | string>;
 33	  color?: boolean;
 34	  parallel?: boolean;
 35	  readyWhen?: string | string[];
 36	  cwd?: string;
 37	  env?: Record<string, string>;
 38	  forwardAllArgs?: boolean; // default is true
 39	  args?: string | string[];
 40	  envFile?: string;
 41	  __unparsed__: string[];
 42	  usePty?: boolean;
 43	  streamOutput?: boolean;
 44	  tty?: boolean;
 45	}
 46	
 47	const propKeys = [
 48	  'command',
 49	  'commands',
 50	  'color',
 51	  'no-color',
 52	  'parallel',
 53	  'no-parallel',
 54	  'readyWhen',
 55	  'cwd',
 56	  'args',
 57	  'envFile',
 58	  '__unparsed__',
 59	  'env',
 60	  'usePty',
 61	  'streamOutput',
 62	  'verbose',
 63	  'forwardAllArgs',
 64	  'tty',
 65	];
 66	
 67	export interface NormalizedRunCommandsOptions extends RunCommandsOptions {
 68	  commands: Array<RunCommandsCommandOptions>;
 69	  unknownOptions?: {
 70	    [k: string]: any;
 71	  };
 72	  parsedArgs: {
 73	    [k: string]: any;
 74	  };
 75	  unparsedCommandArgs?: {
 76	    [k: string]: string | string[];
 77	  };
 78	  args?: string;
 79	  readyWhenStatus: { stringToMatch: string; found: boolean }[];
 80	}
 81	
 82	export default async function (
 83	  options: RunCommandsOptions,
 84	  context: ExecutorContext
 85	): Promise<{
 86	  success: boolean;
 87	  terminalOutput: string;
 88	}> {
 89	  const task = await runCommands(options, context);
 90	  const results = await task.getResults();
 91	  return {
 92	    ...results,
 93	    success: results.code === 0,
 94	  };
 95	}
 96	
 97	export async function runCommands(
 98	  options: RunCommandsOptions,
 99	  context: ExecutorContext
100	) {
101	  const normalized = normalizeOptions(options);
102	
103	  if (normalized.readyWhenStatus.length && !normalized.parallel) {
104	    throw new Error(
105	      'ERROR: Bad executor config for run-commands - "readyWhen" can only be used when "parallel=true".'
106	    );
107	  }
108	
109	  if (
110	    options.commands.find(
111	      (c: any) => c.prefix || c.prefixColor || c.color || c.bgColor
112	    ) &&
113	    !options.parallel
114	  ) {
115	    throw new Error(
116	      'ERROR: Bad executor config for run-commands - "prefix", "prefixColor", "color" and "bgColor" can only be set when "parallel=true".'
117	    );
118	  }
119	
120	  const isSingleCommand = normalized.commands.length === 1;
121	
122	  const usePseudoTerminal =
123	    (isSingleCommand || !options.parallel) && PseudoTerminal.isSupported();
124	
125	  const isSingleCommandAndCanUsePseudoTerminal =
126	    isSingleCommand &&
127	    usePseudoTerminal &&
128	    process.env.NX_NATIVE_COMMAND_RUNNER !== 'false' &&
129	    !normalized.commands[0].prefix &&
130	    normalized.usePty;
131	
132	  const tuiEnabled = isTuiEnabled();
133	
134	  try {
135	    const runningTask = isSingleCommandAndCanUsePseudoTerminal
136	      ? await runSingleCommandWithPseudoTerminal(normalized, context)
137	      : options.parallel
138	      ? new ParallelRunningTasks(normalized, context)
139	      : new SeriallyRunningTasks(normalized, context, tuiEnabled);
140	    return runningTask;
141	  } catch (e) {
142	    if (process.env.NX_VERBOSE_LOGGING === 'true') {
143	      console.error(e);
144	    }
145	    throw new Error(
146	      `ERROR: Something went wrong in run-commands - ${e.message}`
147	    );
148	  }
149	}
150	
151	function normalizeOptions(
152	  options: RunCommandsOptions
153	): NormalizedRunCommandsOptions {
154	  if (options.readyWhen && typeof options.readyWhen === 'string') {
155	    options.readyWhenStatus = [
156	      { stringToMatch: options.readyWhen, found: false },
157	    ];
158	  } else {
159	    options.readyWhenStatus =
160	      (options.readyWhen as string[])?.map((stringToMatch) => ({
161	        stringToMatch,
162	        found: false,
163	      })) ?? [];
164	  }
165	
166	  if (options.command) {
167	    options.commands = [
168	      {
169	        command: Array.isArray(options.command)
170	          ? options.command.join(' ')
171	          : options.command,
172	      },
173	    ];
174	    options.parallel = options.readyWhenStatus?.length > 0;
175	  } else {
176	    options.commands = options.commands.map((c) =>
177	      typeof c === 'string' ? { command: c } : c
178	    );
179	  }
180	
181	  if (options.args && Array.isArray(options.args)) {
182	    options.args = options.args.join(' ');
183	  }
184	
185	  const unparsedCommandArgs = yargsParser(options.__unparsed__, {
186	    configuration: {
187	      'parse-numbers': false,
188	      'parse-positional-numbers': false,
189	      'dot-notation': false,
190	      'camel-case-expansion': false,
191	    },
192	  });
193	  options.unknownOptions = Object.keys(options)
194	    .filter(
195	      (p) => propKeys.indexOf(p) === -1 && unparsedCommandArgs[p] === undefined
196	    )
197	    .reduce((m, c) => ((m[c] = options[c]), m), {});
198	
199	  options.parsedArgs = parseArgs(
200	    unparsedCommandArgs,
201	    options.unknownOptions,
202	    options.args as string
203	  );
204	  options.unparsedCommandArgs = unparsedCommandArgs;
205	
206	  (options as NormalizedRunCommandsOptions).commands.forEach((c) => {
207	    c.command = interpolateArgsIntoCommand(
208	      c.command,
209	      options as NormalizedRunCommandsOptions,
210	      c.forwardAllArgs ?? options.forwardAllArgs ?? true
211	    );
212	  });
213	  return options as NormalizedRunCommandsOptions;
214	}
215	
216	export function interpolateArgsIntoCommand(
217	  command: string,
218	  opts: Pick<
219	    NormalizedRunCommandsOptions,
220	    | 'args'
221	    | 'parsedArgs'
222	    | '__unparsed__'
223	    | 'unknownOptions'
224	    | 'unparsedCommandArgs'
225	  >,
226	  forwardAllArgs: boolean
227	): string {
228	  if (command.indexOf('{args.') > -1) {
229	    const regex = /{args\.([^}]+)}/g;
230	    return command.replace(regex, (_, group: string) =>
231	      opts.parsedArgs[group] !== undefined ? opts.parsedArgs[group] : ''
232	    );
233	  } else if (forwardAllArgs) {
234	    let args = '';
235	    if (Object.keys(opts.unknownOptions ?? {}).length > 0) {
236	      const unknownOptionsArgs = Object.keys(opts.unknownOptions)
237	        .filter(
238	          (k) =>
239	            typeof opts.unknownOptions[k] !== 'object' &&
240	            opts.parsedArgs[k] === opts.unknownOptions[k]
241	        )
242	        .map((k) => `--${k}=${opts.unknownOptions[k]}`)
243	        .map(wrapArgIntoQuotesIfNeeded)
244	        .join(' ');
245	      if (unknownOptionsArgs) {
246	        args += ` ${unknownOptionsArgs}`;
247	      }
248	    }
249	    if (opts.args) {
250	      args += ` ${opts.args}`;
251	    }
252	    if (opts.__unparsed__?.length > 0) {
253	      const filteredParsedOptions = filterPropKeysFromUnParsedOptions(
254	        opts.__unparsed__,
255	        opts.parsedArgs
256	      );
257	      if (filteredParsedOptions.length > 0) {
258	        args += ` ${filteredParsedOptions
259	          .map(wrapArgIntoQuotesIfNeeded)
260	          .join(' ')}`;
261	      }
262	    }
263	    return `${command}${args}`;
264	  } else {
265	    return command;
266	  }
267	}
268	
269	function parseArgs(
270	  unparsedCommandArgs: { [k: string]: string },
271	  unknownOptions: { [k: string]: string },
272	  args?: string
273	) {
274	  if (!args) {
275	    return { ...unknownOptions, ...unparsedCommandArgs };
276	  }
277	
278	  return {
279	    ...unknownOptions,
280	    ...yargsParser(args.replace(/(^"|"$)/g, ''), {
281	      configuration: { 'camel-case-expansion': true },
282	    }),
283	    ...unparsedCommandArgs,
284	  };
285	}
286	
287	/**
288	 * This function filters out the prop keys from the unparsed options
289	 * @param __unparsed__ e.g. ['--prop1', 'value1', '--prop2=value2', '--args=test']
290	 * @param unparsedCommandArgs e.g. { prop1: 'value1', prop2: 'value2', args: 'test'}
291	 * @returns filtered options that are not part of the propKeys array e.g. ['--prop1', 'value1', '--prop2=value2']
292	 */
293	function filterPropKeysFromUnParsedOptions(
294	  __unparsed__: string[],
295	  parseArgs: {
296	    [k: string]: string | string[];
297	  } = {}
298	): string[] {
299	  const parsedOptions = [];
300	  for (let index = 0; index < __unparsed__.length; index++) {
301	    const element = __unparsed__[index];
302	    if (element.startsWith('--')) {
303	      const key = element.replace('--', '');
304	      if (element.includes('=')) {
305	        // key can be in the format of --key=value or --key.subkey=value (e.g. env.foo=bar)
306	        if (!propKeys.includes(key.split('=')[0].split('.')[0])) {
307	          // check if the key is part of the propKeys array
308	          parsedOptions.push(element);
309	        }
310	      } else {
311	        // check if the next element is a value for the key
312	        if (propKeys.includes(key)) {
313	          if (
314	            index + 1 < __unparsed__.length &&
315	            parseArgs[key] &&
316	            __unparsed__[index + 1].toString() === parseArgs[key].toString()
317	          ) {
318	            index++; // skip the next element
319	          }
320	        } else {
321	          parsedOptions.push(element);
322	        }
323	      }
324	    } else {
325	      parsedOptions.push(element);
326	    }
327	  }
328	  return parsedOptions;
329	}
330	
331	function wrapArgIntoQuotesIfNeeded(arg: string): string {
332	  if (arg.includes('=')) {
333	    const [key, value] = arg.split('=');
334	    if (
335	      key.startsWith('--') &&
336	      value.includes(' ') &&
337	      !(value[0] === "'" || value[0] === '"')
338	    ) {
339	      return `${key}="${value}"`;
340	    }
341	    return arg;
342	  } else if (arg.includes(' ') && !(arg[0] === "'" || arg[0] === '"')) {
343	    return `"${arg}"`;
344	  } else {
345	    return arg;
346	  }
347	}
