  1	import * as chalk from 'chalk';
  2	import { ChildProcess, exec, Serializable } from 'child_process';
  3	import { env as appendLocalEnv } from 'npm-run-path';
  4	import { isAbsolute, join } from 'path';
  5	import * as treeKill from 'tree-kill';
  6	import { ExecutorContext } from '../../config/misc-interfaces';
  7	import {
  8	  createPseudoTerminal,
  9	  PseudoTerminal,
 10	  PseudoTtyProcess,
 11	} from '../../tasks-runner/pseudo-terminal';
 12	import { RunningTask } from '../../tasks-runner/running-tasks/running-task';
 13	import {
 14	  loadAndExpandDotEnvFile,
 15	  unloadDotEnvFile,
 16	} from '../../tasks-runner/task-env';
 17	import { signalToCode } from '../../utils/exit-codes';
 18	import {
 19	  LARGE_BUFFER,
 20	  NormalizedRunCommandsOptions,
 21	  RunCommandsCommandOptions,
 22	} from './run-commands.impl';
 23	
 24	export class ParallelRunningTasks implements RunningTask {
 25	  private readonly childProcesses: RunningNodeProcess[];
 26	  private readyWhenStatus: { stringToMatch: string; found: boolean }[];
 27	  private readonly streamOutput: boolean;
 28	
 29	  private exitCallbacks: Array<(code: number, terminalOutput: string) => void> =
 30	    [];
 31	  private outputCallbacks: Array<(terminalOutput: string) => void> = [];
 32	
 33	  constructor(options: NormalizedRunCommandsOptions, context: ExecutorContext) {
 34	    this.childProcesses = options.commands.map(
 35	      (commandConfig) =>
 36	        new RunningNodeProcess(
 37	          commandConfig,
 38	          options.color,
 39	          calculateCwd(options.cwd, context),
 40	          options.env ?? {},
 41	          options.readyWhenStatus,
 42	          options.streamOutput,
 43	          options.envFile
 44	        )
 45	    );
 46	    this.readyWhenStatus = options.readyWhenStatus;
 47	    this.streamOutput = options.streamOutput;
 48	
 49	    this.run();
 50	  }
 51	
 52	  async getResults(): Promise<{ code: number; terminalOutput: string }> {
 53	    return new Promise((res) => {
 54	      this.onExit((code, terminalOutput) => {
 55	        res({ code, terminalOutput });
 56	      });
 57	    });
 58	  }
 59	
 60	  onOutput(cb: (terminalOutput: string) => void) {
 61	    this.outputCallbacks.push(cb);
 62	  }
 63	
 64	  onExit(cb: (code: number, terminalOutput: string) => void): void {
 65	    this.exitCallbacks.push(cb);
 66	  }
 67	
 68	  send(message: Serializable): void {
 69	    for (const childProcess of this.childProcesses) {
 70	      childProcess.send(message);
 71	    }
 72	  }
 73	
 74	  async kill(signal?: NodeJS.Signals) {
 75	    await Promise.all(
 76	      this.childProcesses.map(async (p) => {
 77	        try {
 78	          return p.kill();
 79	        } catch (e) {
 80	          console.error(`Unable to terminate "${p.command}"\nError:`, e);
 81	        }
 82	      })
 83	    );
 84	  }
 85	
 86	  private async run() {
 87	    if (this.readyWhenStatus.length) {
 88	      let {
 89	        childProcess,
 90	        result: { code, terminalOutput },
 91	      } = await Promise.race(
 92	        this.childProcesses.map(
 93	          (childProcess) =>
 94	            new Promise<{
 95	              childProcess: RunningNodeProcess;
 96	              result: { code: number; terminalOutput: string };
 97	            }>((res) => {
 98	              childProcess.onOutput((terminalOutput) => {
 99	                for (const cb of this.outputCallbacks) {
100	                  cb(terminalOutput);
101	                }
102	              });
103	              childProcess.onExit((code, terminalOutput) => {
104	                res({
105	                  childProcess,
106	                  result: { code, terminalOutput },
107	                });
108	              });
109	            })
110	        )
111	      );
112	
113	      if (code !== 0) {
114	        const output = `Warning: command "${childProcess.command}" exited with non-zero status code`;
115	        terminalOutput += output;
116	        if (this.streamOutput) {
117	          process.stderr.write(output);
118	        }
119	      }
120	
121	      for (const cb of this.exitCallbacks) {
122	        cb(code, terminalOutput);
123	      }
124	    } else {
125	      const results = await Promise.all(
126	        this.childProcesses.map(async (childProcess) => {
127	          childProcess.onOutput((terminalOutput) => {
128	            for (const cb of this.outputCallbacks) {
129	              cb(terminalOutput);
