144	      const failed = results.filter((result) => result.result.code !== 0);
145	      if (failed.length > 0) {
146	        const output = failed
147	          .map(
148	            (failedResult) =>
149	              `Warning: command "${failedResult.childProcess.command}" exited with non-zero status code`
150	          )
151	          .join('\r\n');
152	        terminalOutput += output;
153	        if (this.streamOutput) {
154	          process.stderr.write(output);
155	        }
156	
157	        for (const cb of this.exitCallbacks) {
158	          cb(1, terminalOutput);
159	        }
160	      } else {
161	        for (const cb of this.exitCallbacks) {
162	          cb(0, terminalOutput);
163	        }
164	      }
165	    }
166	  }
