307	class RunningNodeProcess implements RunningTask {
308	  private terminalOutput = '';
309	  private childProcess: ChildProcess;
310	  private exitCallbacks: Array<(code: number, terminalOutput: string) => void> =
311	    [];
312	  private outputCallbacks: Array<(terminalOutput: string) => void> = [];
313	  public command: string;
314	
315	  constructor(
316	    commandConfig: RunCommandsCommandOptions,
317	    color: boolean,
318	    cwd: string,
319	    env: Record<string, string>,
320	    private readyWhenStatus: { stringToMatch: string; found: boolean }[],
321	    streamOutput = true,
322	    envFile: string
323	  ) {
324	    env = processEnv(color, cwd, env, envFile);
325	    this.command = commandConfig.command;
326	    this.terminalOutput = chalk.dim('> ') + commandConfig.command + '\r\n\r\n';
327	    if (streamOutput) {
328	      process.stdout.write(this.terminalOutput);
329	    }
330	    this.childProcess = exec(commandConfig.command, {
331	      maxBuffer: LARGE_BUFFER,
332	      env,
333	      cwd,
334	      windowsHide: false,
335	    });
336	
337	    this.addListeners(commandConfig, streamOutput);
338	  }
339	
340	  getResults(): Promise<{ code: number; terminalOutput: string }> {
341	    return new Promise((res) => {
342	      this.onExit((code, terminalOutput) => {
343	        res({ code, terminalOutput });
344	      });
345	    });
346	  }
