360	  kill(signal?: NodeJS.Signals): Promise<void> {
361	    return new Promise<void>((res, rej) => {
362	      treeKill(this.childProcess.pid, signal, (err) => {
363	        // On Windows, tree-kill (which uses taskkill) may fail when the process or its child process is already terminated.
364	        // Ignore the errors, otherwise we will log them unnecessarily.
365	        if (err && process.platform !== 'win32') {
366	          rej(err);
367	        } else {
368	          res();
369	        }
370	      });
371	    });
372	  }
