374	  private triggerOutputListeners(output: string) {
375	    for (const cb of this.outputCallbacks) {
376	      cb(output);
377	    }
378	  }
379	
380	  private addListeners(
381	    commandConfig: RunCommandsCommandOptions,
382	    streamOutput: boolean
383	  ) {
384	    this.childProcess.stdout.on('data', (data) => {
385	      const output = addColorAndPrefix(data, commandConfig);
386	
387	      this.terminalOutput += output;
388	      this.triggerOutputListeners(output);
389	
390	      if (streamOutput) {
391	        process.stdout.write(output);
392	      }
393	      if (
394	        this.readyWhenStatus.length &&
395	        isReady(this.readyWhenStatus, data.toString())
396	      ) {
397	        for (const cb of this.exitCallbacks) {
398	          cb(0, this.terminalOutput);
399	        }
400	      }
401	    });
402	    this.childProcess.stderr.on('data', (err) => {
403	      const output = addColorAndPrefix(err, commandConfig);
404	
405	      this.terminalOutput += output;
406	      this.triggerOutputListeners(output);
407	
408	      if (streamOutput) {
409	        process.stderr.write(output);
410	      }
411	      if (
412	        this.readyWhenStatus.length &&
413	        isReady(this.readyWhenStatus, err.toString())
414	      ) {
415	        for (const cb of this.exitCallbacks) {
416	          cb(1, this.terminalOutput);
417	        }
418	      }
419	    });
420	    this.childProcess.on('error', (err) => {
421	      const output = addColorAndPrefix(err.toString(), commandConfig);
422	      this.terminalOutput += output;
423	      if (streamOutput) {
424	        process.stderr.write(output);
425	      }
426	      for (const cb of this.exitCallbacks) {
427	        cb(1, this.terminalOutput);
428	      }
429	    });
430	    this.childProcess.on('exit', (code) => {
431	      if (!this.readyWhenStatus.length || isReady(this.readyWhenStatus)) {
432	        for (const cb of this.exitCallbacks) {
433	          cb(code, this.terminalOutput);
434	        }
435	      }
436	    });
437	  }
