440	export async function runSingleCommandWithPseudoTerminal(
441	  normalized: NormalizedRunCommandsOptions,
442	  context: ExecutorContext
443	): Promise<PseudoTtyProcess> {
444	  const pseudoTerminal = createPseudoTerminal();
445	  const pseudoTtyProcess = await createProcessWithPseudoTty(
446	    pseudoTerminal,
447	    normalized.commands[0],
448	    normalized.color,
449	    calculateCwd(normalized.cwd, context),
450	    normalized.env,
451	    normalized.streamOutput,
452	    pseudoTerminal ? normalized.isTTY : false,
453	    normalized.envFile
454	  );
455	  registerProcessListener(pseudoTtyProcess, pseudoTerminal);
456	  return pseudoTtyProcess;
457	}
