459	async function createProcessWithPseudoTty(
460	  pseudoTerminal: PseudoTerminal,
461	  commandConfig: RunCommandsCommandOptions,
462	  color: boolean,
463	  cwd: string,
464	  env: Record<string, string>,
465	  streamOutput: boolean = true,
466	  tty: boolean,
467	  envFile?: string
468	) {
469	  return pseudoTerminal.runCommand(commandConfig.command, {
470	    cwd,
471	    jsEnv: processEnv(color, cwd, env, envFile),
472	    quiet: !streamOutput,
473	    tty,
474	  });
475	}
476	
477	function addColorAndPrefix(out: string, config: RunCommandsCommandOptions) {
478	  if (config.prefix) {
479	    out = out
480	      .split('\n')
481	      .map((l) => {
482	        let prefixText = config.prefix;
483	        if (config.prefixColor && chalk[config.prefixColor]) {
484	          prefixText = chalk[config.prefixColor](prefixText);
485	        }
486	        prefixText = chalk.bold(prefixText);
487	        return l.trim().length > 0 ? `${prefixText} ${l}` : l;
488	      })
489	      .join('\n');
490	  }
491	  if (config.color && chalk[config.color]) {
492	    out = chalk[config.color](out);
493	  }
494	  if (config.bgColor && chalk[config.bgColor]) {
495	    out = chalk[config.bgColor](out);
496	  }
497	  return out;
498	}
499	
500	function calculateCwd(
501	  cwd: string | undefined,
502	  context: ExecutorContext
503	): string {
504	  if (!cwd) return context.root;
505	  if (isAbsolute(cwd)) return cwd;
506	  return join(context.root, cwd);
507	}
508	
509	/**
510	 * Env variables are processed in the following order:
511	 * - env option from executor options
512	 * - env file from envFile option if provided
513	 * - local env variables
514	 */
515	function processEnv(
516	  color: boolean,
517	  cwd: string,
518	  envOptionFromExecutor: Record<string, string>,
519	  envFile?: string
520	) {
521	  let localEnv = appendLocalEnv({ cwd: cwd ?? process.cwd() });
522	  localEnv = {
523	    ...process.env,
524	    ...localEnv,
525	  };
526	
527	  if (process.env.NX_LOAD_DOT_ENV_FILES !== 'false' && envFile) {
528	    loadEnvVarsFile(envFile, localEnv);
529	  }
530	  let res: Record<string, string> = {
531	    ...localEnv,
532	    ...envOptionFromExecutor,
533	  };
534	  // need to override PATH to make sure we are using the local node_modules
535	  if (localEnv.PATH) res.PATH = localEnv.PATH; // UNIX-like
536	  if (localEnv.Path) res.Path = localEnv.Path; // Windows
537	
538	  if (color) {
539	    res.FORCE_COLOR = `${color}`;
540	  }
541	  return res;
542	}
543	
544	function isReady(
545	  readyWhenStatus: { stringToMatch: string; found: boolean }[] = [],
546	  data?: string
547	): boolean {
548	  if (data) {
549	    for (const readyWhenElement of readyWhenStatus) {
550	      if (data.toString().indexOf(readyWhenElement.stringToMatch) > -1) {
551	        readyWhenElement.found = true;
552	        break;
553	      }
554	    }
555	  }
556	
557	  return readyWhenStatus.every((readyWhenElement) => readyWhenElement.found);
558	}
559	
560	function loadEnvVarsFile(path: string, env: Record<string, string> = {}) {
561	  unloadDotEnvFile(path, env);
562	  const result = loadAndExpandDotEnvFile(path, env);
563	  if (result.error) {
564	    throw result.error;
565	  }
566	}
567	
568	let registered = false;
569	
570	function registerProcessListener(
571	  runningTask: PseudoTtyProcess | ParallelRunningTasks | SeriallyRunningTasks,
572	  pseudoTerminal?: PseudoTerminal
573	) {
574	  if (registered) {
575	    return;
576	  }
577	
578	  registered = true;
579	  // When the nx process gets a message, it will be sent into the task's process
580	  process.on('message', (message: Serializable) => {
581	    // this.publisher.publish(message.toString());
582	    if (pseudoTerminal) {
583	      pseudoTerminal.sendMessageToChildren(message);
584	    }
585	
586	    if ('send' in runningTask) {
587	      runningTask.send(message);
588	    }
589	  });
590	
591	  // Terminate any task processes on exit
592	  process.on('exit', () => {
593	    runningTask.kill();
594	  });
595	  process.on('SIGINT', () => {
596	    runningTask.kill('SIGTERM');
597	    // we exit here because we don't need to write anything to cache.
598	    process.exit(signalToCode('SIGINT'));
599	  });
600	  process.on('SIGTERM', () => {
601	    runningTask.kill('SIGTERM');
602	    // no exit here because we expect child processes to terminate which
603	    // will store results to the cache and will terminate this process
604	  });
605	  process.on('SIGHUP', () => {
606	    runningTask.kill('SIGTERM');
607	    // no exit here because we expect child processes to terminate which
608	    // will store results to the cache and will terminate this process
609	  });
610	}
